(514, '<script type="text/javascript" src="<{$xoops_url}>/modules/formulize/jquery/jquery-1.4.2.min.js"></script>\r\n<script type="text/javascript" src="<{$xoops_url}>/modules/formulize/jquery/jquery-ui-1.8.2.custom.min.js"></script>\r\n\r\n<link rel="stylesheet" type="text/css" href="<{$xoops_url}>/modules/formulize/jquery/css/start/jquery-ui-1.8.2.custom.css">\r\n<link rel="stylesheet" type="text/css" href="<{$xoops_url}>/modules/formulize/templates/admin/css/ui.css">\r\n<script type="text/javascript">\r\n var pagehasaccordion = new Array();\r\n</script>\r\n\r\n<!-- jquery for floating admin bar, added by S.Gray, April 11, 2011 -->\r\n<script type="text/javascript">\r\n$(document).ready(function() {\r\n	var offset = $(''#admin_toolbar'').offset();\r\n	\r\n	  $(window).scroll(function () {\r\n		var scrollTop = $(window).scrollTop();\r\n		if (offset.top<scrollTop) {\r\n		  $(''#admin_toolbar'').addClass(''toolbar_fixed'');\r\n		  $(''#admin_toolbar'').addClass(''ui-corner-all'');\r\n		} else {\r\n		  $(''#admin_toolbar'').removeClass(''toolbar_fixed'');\r\n		  $(''#admin_toolbar'').removeClass(''ui-corner-all'');\r\n		};\r\n	  });\r\n		\r\n	});\r\n</script>\r\n<!-- end jquery -->\r\n\r\n<!--[if IE 6]> \r\n	<link rel="stylesheet" type="text/css" href="<{$xoops_url}>/modules/formulize/templates/admin/css/ie6.css" />\r\n<![endif]-->\r\n\r\n<div class="admin-ui">\r\n\r\n<form name="scrollposition" method="post" action="">\r\n  <input type="hidden" name="scrollx" value="">\r\n  <input type="hidden" name="tabs_selected" value="">\r\n  <input type="hidden" name="accordion_active" value="">\r\n</form>\r\n\r\n<{if $breadcrumbtrail}>\r\n<p>\r\n\r\n<{counter start=0 assign=crumbcount print=false}>\r\n<{foreach from=$breadcrumbtrail item=crumb}>\r\n\r\n<{if $crumbcount}>\r\n >\r\n<{/if}>\r\n\r\n<{if $crumb.url}>\r\n <a href="ui.php?<{$crumb.url}>"><{$crumb.text}></a>\r\n <{assign var=''lasturl'' value=$crumb.url}>\r\n <{assign var=''lasttext'' value=$crumb.text}>\r\n<{else}>\r\n <{$crumb.text}>\r\n<{/if}>\r\n  \r\n<{counter}>\r\n<{/foreach}>\r\n\r\n</p>\r\n<{/if}>\r\n  \r\n<div id="formulize-logo"><img src="<{$xoops_url}><{$adminPage.logo}>" align="<{$adminPage.pagetitle}>" title="<{$adminPage.pagetitle}>" /></div>\r\n\r\n<{if $adminPage.pagetitle}>\r\n<h1><{$adminPage.pagetitle}></h1>\r\n<p class="smallhead"><{$adminPage.pagesubtitle}></p>\r\n<{if $lasturl}>\r\n<p><a href="ui.php?<{$lasturl}>">Go Back to <i><{$lasttext}></i></a></p>\r\n<{/if}>\r\n<{/if}>\r\n\r\n<!-- modified by Freeform Solutions, S.Gray, April 11, 2011 -->\r\n<div id="admin_toolbar">\r\n<{if $adminPage.needsave}>\r\n<div id=savebutton><input type="button" class="savebutton" value="Save your changes"/></div>\r\n<{/if}>\r\n<div id="savewarning" class="ui-corner-all">You have unsaved changes!</div>\r\n</div> <!-- /admin_toolbar -->\r\n\r\n<{if $adminPage.template}>\r\n<{include file=$adminPage.template}>  \r\n<{/if}>\r\n\r\n<{if $adminPage.tabs}>  \r\n<{include file="db:admin/ui-tabs.html" tabs=$adminPage.tabs}>\r\n<{/if}>\r\n  \r\n<p class="versionnumber">Version 4.0 RC4</p>\r\n\r\n</div><!-- End admin-ui -->\r\n\r\n<script type="text/javascript">\r\n\r\n  var saveCounter = 0;\r\n  var saveTarget = 0;\r\n  var redirect = "";\r\n  var newhandle = "";\r\n  var formdata = new Array();\r\n\r\n  $("input").change(function() {\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n    });\r\n  $("input[type=text]").keydown(function() {\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n    });\r\n  $("select").change(function() {\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n    });\r\n  $("textarea").keydown(function() {\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n    });\r\n\r\n  $(".savebutton").click(function() {\r\n    if(validateRequired()) {\r\n      runSaveEvent();\r\n    }\r\n  });\r\n\r\n  function runSaveEvent() {\r\n    $(".admin-ui").fadeTo(1,0.5);\r\n    var formulize_formlist = $(".formulize-admin-form");\r\n    saveCounter = 0;\r\n    saveTarget = 0;\r\n    redirect = "";\r\n    formdata = new Array();\r\n    for( var i in formulize_formlist) {\r\n      if(typeof(formulize_formlist[i]) == ''object'') { // for some crazy reason, non-form stuff can be pulled in by getElementsByName with that param...I hate javascript\r\n        formdata[saveTarget] = formulize_formlist[i];\r\n        saveTarget = saveTarget + 1;\r\n      }\r\n    }\r\n    if(saveTarget > 0) {\r\n      sendFormData(formdata[0]); // send the first form''s data \r\n    }\r\n  }\r\n  \r\n  function sendFormData(thisformdata, ele_id) {\r\n    if(!ele_id) { ele_id = 0 }\r\n    $.post("save.php?ele_id="+ele_id, $(thisformdata).serialize(), function(data) {\r\n      saveCounter = saveCounter + 1;\r\n      if(data) {\r\n        if(data.substr(0,10)=="/* eval */") {\r\n          redirect = data;\r\n        } else if(data.substr(0,13)=="/* evalnow */") {\r\n          eval(data);\r\n        } else {\r\n          alert(data);\r\n        }\r\n      }\r\n      if(saveCounter >= saveTarget) { // if we''ve received a response for all the forms...\r\n        window.document.getElementById(''savewarning'').style.display = ''none'';\r\n        $(".savebutton").blur();\r\n        if(newhandle) {\r\n          $("[name=original_handle]").val(newhandle);\r\n        }\r\n        if(redirect) {\r\n          eval(redirect);\r\n        } else {\r\n          $(".admin-ui").fadeTo(1,1);\r\n        }\r\n      } else { // if there''s still forms to do, then send the next one...must do sequentially to avoid race conditions\r\n        sendFormData(formdata[saveCounter], ele_id);\r\n      }\r\n    });\r\n  }\r\n  \r\n  function reloadWithScrollPosition(url) {\r\n    if(url) {\r\n      $("[name=scrollposition]").attr(''action'', url);\r\n    }\r\n    window.document.scrollposition.scrollx.value = $(window).scrollTop();\r\n    var tabs_selected = "";\r\n    <{if $adminPage.tabs}> \r\n    tabs_selected = $("#tabs").tabs("option","selected");\r\n    window.document.scrollposition.tabs_selected.value = tabs_selected;\r\n    tabs_selected = tabs_selected+1;\r\n    <{/if}>\r\n    var accordion_active = "";\r\n    if(pagehasaccordion["accordion-"+tabs_selected]) {\r\n      <{* // not really the active accordion we want, it''s the current position of the active accordion, since accordion sections are sortable! - this is a semi-rare bug that needs fixing, cross reference the active position with the results of a toArray call on the sortable element *}>\r\n      accordion_active = $("#accordion-"+tabs_selected).accordion( "option", "active" );\r\n    }\r\n    window.document.scrollposition.accordion_active.value = accordion_active;\r\n    window.document.scrollposition.submit();\r\n  }\r\n\r\n  function validateRequired() {\r\n    var requiredok = true;\r\n    $(".required_formulize_element").each(function () {\r\n      if(($(this).val().length) == 0) {\r\n        requiredok = false;\r\n      }\r\n    });\r\n    return requiredok;\r\n  }\r\n\r\n  $().ajaxError(function () {\r\n    alert("There was an error when saving your data.  Please try again.");\r\n  });\r\n  \r\n  $(window).load(function () {\r\n    $(window).scrollTop(<{$scrollx}>);\r\n  });\r\n  \r\n</script>'),
(515, '<script type="text/javascript">\r\n	\r\n	$(function() {\r\n		$("#tabs").tabs({\r\n			<{if $adminPage.tabselected !== ""}>\r\n			selected: <{$adminPage.tabselected}>\r\n			<{/if}>\r\n		});\r\n	});\r\n	\r\n</script>\r\n\r\n<div id="tabs">\r\n	<ul>\r\n		<{foreach from=$tabs key=number item=tab}>\r\n		<li><a href="#tabs-<{$number}>"><{$tab.name}></a></li>\r\n		<{/foreach}>\r\n	</ul>\r\n	<{foreach from=$tabs key=number item=tab}>\r\n	<div id="tabs-<{$number}>" class="tab-content">\r\n		<{include file=$tab.template content=$tab.content}>\r\n	</div>\r\n	<{/foreach}>\r\n</div><!-- end tabs -->'),
(516, '<script type="text/javascript">\r\n\r\n	pagehasaccordion["accordion-<{$number}>"] = true;\r\n\r\n	$(function() {\r\n		var stop = false;\r\n		$("#accordion-<{$number}> h3").click(function(event) {\r\n			if (stop) {\r\n				event.stopImmediatePropagation();\r\n				event.preventDefault();\r\n				stop = false;\r\n			}\r\n		});\r\n		$("#accordion-<{$number}>").accordion({\r\n			autoHeight: false, // no fixed height for sections\r\n			collapsible: true, // sections can be collapsed \r\n			active: <{$accordion_active}>,\r\n			header: "> div > h3"\r\n		}).sortable({\r\n			axis: "y",\r\n			handle: "h3",\r\n			stop: function(event, ui) {\r\n				stop = true;\r\n			}\r\n		});\r\n	});\r\n\r\n</script>\r\n\r\n<div id="accordion-<{$number}>">\r\n	<{foreach from=$sections key=sectionNumber item=section}>\r\n	<div id="drawer-<{$number}>-<{$sectionNumber}>">\r\n    <h3><a href="#"><span class="accordion-name"><{$section.name}></span></a></h3>\r\n		<div class="accordion-content content">\r\n			<{include file=$sectionTemplate sectionContent=$section.content}>\r\n		</div>\r\n	</div>\r\n	<{/foreach}>\r\n</div><!-- end accordion -->'),
(517, '<div class="panel-content content">\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<{php}>print $GLOBALS[''xoopsSecurity'']->getTokenHTML()<{/php}>\r\n<input type="hidden" name="formulize_admin_handler" value="application_settings">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.aid}>">\r\n<input type="hidden" id="reload_settings" name="reload_settings" value="">\r\n\r\n	<div class="accordion-box">\r\n		<div class="form-item">\r\n        	<fieldset>\r\n                <legend><label for="applications-name" class="question">What is the name of this Application?</label></legend>\r\n                <input type="text" id="applications-name" name="applications-name" value="<{$content.name}>" class="input-text" />\r\n            </fieldset>\r\n		</div>\r\n\r\n		<div class="form-item">\r\n        	<fieldset>\r\n                <legend><label for="applications-description" class="question">Description of this application:</label></legend>\r\n                <textarea rows="4" id="applications-description" name="applications-description"><{$content.description}></textarea>\r\n            </fieldset>\r\n		</div>\r\n	\r\n	</div>\r\n	\r\n	<div class="accordion-box">\r\n		<div class="form-item">\r\n			<fieldset>\r\n				<legend><label for="applications-forms" class="question">Forms in this application:</label></legend>\r\n				<select id="applications-forms" name="applications-forms[]" size=6 multiple>\r\n					<{foreach from=$content.forms item=thisform}>\r\n						<option value=<{$thisform.id}><{$thisform.selected}>><{$thisform.name}></option>\r\n					<{/foreach}>\r\n				</select>\r\n			</fieldset>\r\n		</div>\r\n	</div>\r\n	\r\n\r\n</form>\r\n\r\n<div style="clear: both"></div>\r\n</div> <!--// end content -->\r\n\r\n<script type="text/javascript">\r\n\r\n	$("#applications-name").keydown(function () {\r\n		window.document.getElementById(''reload_settings'').value = 1;\r\n	});\r\n	\r\n</script>'),
(518, '<div id="formlisting">\r\n	<{include file="db:admin/form_listing.html" sectionContent=$content}>\r\n</div>'),
(519, '<{foreach from=$sectionContent.forms item=form}>\r\n  \r\n<{if $form.istableform}>\r\n<{assign var="defaulttab" value="settings"}>\r\n<{else}>\r\n<{assign var="defaulttab" value="elements"}>    \r\n<{/if}>\r\n  \r\n<div class="accordion-box">\r\n  <p class="form-name ui-corner-top"><{if $form.lockedform != 1}><a href="ui.php?page=form&aid=<{$sectionContent.aid}>&fid=<{$form.fid}>&tab=<{$defaulttab}>" class="form-name-icon"><{/if}>Form: <{$form.name}> (id: <{$form.fid}>)<{if $form.lockedform != 1}></a><{/if}></p>\r\n  <div class="form-screen-list ui-corner-bottom">\r\n		<p class="formbox-title">Actions:</p>\r\n		<div class="formactions-list">\r\n		<{if $form.lockedform != 1}>\r\n		<a href="ui.php?page=form&aid=<{$sectionContent.aid}>&fid=<{$form.fid}>&tab=<{$defaulttab}>"><div class="imageicon"><img src="../images/kedit.png"></div><div class="formaction">Configure</div></a><br />\r\n		<a href="../index.php?fid=<{$form.fid}>" target="_blank"><div class="imageicon"><img src="../images/kfind.png"></div><div class="formaction">View</div></a><br />\r\n		<{/if}>\r\n		<a class="cloneform" target="<{$form.fid}>" href=""><div class="imageicon"><img src="../images/clone.gif"></div><div class="formaction">Clone</div></a><br />\r\n		<a class="cloneformdata" target="<{$form.fid}>" href=""><div class="imageicon"><img src="../images/clonedata.gif"></div><div class="formaction">Clone with data</div></a><br />\r\n		<{if $form.lockedform != 1 && $form.hasdelete}>\r\n		<a class="lockdown" target="<{$form.fid}>" href=""><div class="imageicon"><img src="../images/perm.png"></div><div class="formaction">Lockdown</div></a><br />\r\n		<a class="deletelink" target="<{$form.fid}>" href=""><div class="imageicon"><img src="../images/editdelete.gif"></div><div class="formaction">Delete</div></a><br />\r\n		<{/if}>\r\n		</div>\r\n		\r\n		<{if !$form.defaultformscreenid && !$form.defaultlistscreenid}>\r\n    <p><a href="ui.php?page=screen&aid=<{$sectionContent.aid}>&fid=<{$form.fid}>&sid=new"><div class="imageicon"><img src="..images/filenew2.png"></div><div class="formaction">Create a new screen</div></a></p>\r\n		<{else}>\r\n		<p class="formbox-title">Default Screens:</p>\r\n		<div class="formactions-list">\r\n		<{if $form.defaultlistscreenid}>\r\n		<a href="ui.php?page=screen&aid=<{$sectionContent.aid}>&fid=<{$form.fid}>&sid=<{$form.defaultlistscreenid}>"><div class="imageicon"><img src="../images/kedit.png"></div><div class="formaction"><{$form.defaultlistscreenname}></div></a><br />\r\n		<{/if}>\r\n		<{if $form.defaultformscreenid}>  \r\n		<a href="ui.php?page=screen&aid=<{$sectionContent.aid}>&fid=<{$form.fid}>&sid=<{$form.defaultformscreenid}>"><div class="imageicon"><img src="../images/kedit.png"></div><div class="formaction"><{$form.defaultformscreenname}></div></a><br />\r\n		<{/if}>\r\n		<a href="ui.php?page=form&aid=<{$sectionContent.aid}>&fid=<{$form.fid}>&tab=screens"><div class="formaction"><i>...more screens</i></div></a>\r\n		</div>	\r\n    <{/if}>\r\n	</div>\r\n</div>\r\n<{/foreach}>'),
(520, '<div class="panel-content content">\r\n\r\n\r\n<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<{php}>print $GLOBALS[''xoopsSecurity'']->getTokenHTML()<{/php}>\r\n<input type="hidden" name="formulize_admin_handler" value="form_settings">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.fid}>">\r\n<input type="hidden" name="application_url_id" value="<{$content.aid}>">\r\n<input type="hidden" id="reload_settings" name="reload_settings" value="<{if $content.fid == ''new''}>1<{/if}>">\r\n\r\n	<div class="accordion-box">\r\n		<div class="form-item required">\r\n        	<fieldset>\r\n                <legend><label for="forms-title" class="question">What is the name of the form?</label></legend>\r\n                <p>Form title: <input type="text" id="forms-title" name="forms-title" class="required_formulize_element" value="<{$content.name}>" /></p>\r\n								<p>Menu entry: <input type="text" id="forms-menutext" name="forms-menutext" value="<{$content.menutext}>"  /></p>\r\n								<div class="description">\r\n									Leave the "menu entry" blank to remove this form from the default menu block\r\n								</div>\r\n            </fieldset>\r\n		</div>\r\n	</div>\r\n\r\n  <div class="accordion-box">\r\n	  <div class="form-item">\r\n     	<fieldset>\r\n        <legend><label for="forms-form_handle" class="question">Form handle</label></legend>\r\n			  <input type="text" name="forms-form_handle" value="<{$content.form_handle}>">\r\n			  <div class="description">\r\n				  <p>Optional. The name will you use to refer to this form in programming code and in the database. Defaults to the form ID number.</p>\r\n			  </div>\r\n		  </fieldset>\r\n	  </div>\r\n  </div>\r\n\r\n		<{if $content.istableform == true && $content.fid == "new"}>\r\n		<div class="accordion-box">\r\n			<div class="form-item">\r\n				<fieldset>\r\n                    <legend><label for="database_table" class="question">Which database table should this "form" point to?</label></legend>\r\n                    <input type="text" id="forms-tableform" name="forms-tableform" value="" />\r\n                    <div class="description">\r\n                        <p>Type the exact name, including the prefix, ie: mysite_groups</p>\r\n                    </div>\r\n                </fieldset>\r\n			</div>\r\n		</div>\r\n		<div style="clear: both;"></div>\r\n		<{/if}>\r\n	\r\n		<{if $content.istableform == false}>\r\n		<div class="accordion-box">\r\n			<div class="form-item">\r\n        	<fieldset>\r\n				<legend><label class="question">How many entries are allowed in this form?</label></legend>\r\n				\r\n				<div class="form-radios">\r\n					<label for="group"><input type="radio" id="group" name="forms-single" value="group" />One entry per <b>group</b></label>\r\n				</div>\r\n				<div class="form-radios">\r\n					<label for="on"><input type="radio" id="user" name="forms-single" value="user" />One entry per <b>user</b></label>\r\n				</div>\r\n				<div class="form-radios">\r\n					<label for="empty"><input type="radio" id="off" name="forms-single" value="off" /><b>More than one entry</b> per user</label>\r\n				</div>\r\n             </fieldset>\r\n		</div>\r\n	</div>\r\n	<div style="clear: both;"></div>\r\n	\r\n		<{if $content.fid != "new" && $content.elementheadings|@count}>\r\n		<div class="accordion-box">\r\n			<div class="form-item">\r\n				<fieldset>\r\n									<legend><label class="question">When showing a list of entries in this form, which elements should be displayed by default?</label></legend>\r\n									<select name=headerlist[] size=10 multiple class="form-multiple-select">\r\n											<{foreach from=$content.elementheadings item=element}>\r\n											<option value=<{$element.ele_id}><{$element.selected}>><{$element.text}></option>\r\n											<{/foreach}>\r\n									</select>\r\n							</fieldset>\r\n			</div>\r\n		</div>\r\n		<{/if}>\r\n	\r\n	<{/if}>\r\n	\r\n	<{if $content.applications|@count > 0}>\r\n	<div class="accordion-box">\r\n		<div class="form-item">\r\n			<fieldset>\r\n                <legend><label class="question">Which applications is this form part of?</label></legend>\r\n                <select name="apps[]" id="apps" size=10 multiple>\r\n                    <{foreach from=$content.applications item=application}>\r\n                        <option value=<{$application.appid}><{$application.selected}>><{$application.text}></option>\r\n                    <{/foreach}>\r\n                </select>\r\n            </fieldset>\r\n		</div>\r\n	</div>\r\n	<{/if}>\r\n	<div class="accordion-box">\r\n		<div class="form-item">\r\n			<fieldset>\r\n                <legend><label class="question">Create a new application for this form to be part of?</label></legend>\r\n                <div class="form-radios radio-inline">\r\n                    <label for="yes"><input type="radio" id="new-app-yes" name="new_app_yes_no" value="yes"/>Yes</label>\r\n                </div>\r\n                <div class="form-radios radio-inline">\r\n                    <label for="yes"><input type="radio" id="new-app-no" name="new_app_yes_no" value="no" checked/>No</label>\r\n                </div>\r\n								<br /><br />\r\n								<div class="form-item" id="new-application-box" style="display: none;">\r\n			          <label class="question">What is the name of the new application?</label>\r\n                <input type="text" id="applications-name" name="applications-name" value="" />\r\n							</div>\r\n            </fieldset>\r\n		</div>\r\n	</div> \r\n\r\n</form>\r\n<script type="text/javascript">\r\n  $("#<{$content.singleentry}>").attr(''checked'', true);\r\n	\r\n	$("#forms-title").keydown(function () {\r\n		window.document.getElementById(''reload_settings'').value = 1;\r\n	});\r\n	\r\n	$(''input:radio[name=new_app_yes_no]'').change(function(){\r\n		if($(''input:radio[name=new_app_yes_no]:checked'').val() == ''yes'') {\r\n			window.document.getElementById("new-application-box").style.display = ''block'';\r\n		} else {\r\n			window.document.getElementById("new-application-box").style.display = ''none'';\r\n		}\r\n		});\r\n	\r\n	$(".savebutton").click(function() {\r\n	  if($("[name=forms-title]").val() == "") {\r\n			alert("Forms must have a name!");\r\n			$("[name=forms-title]").focus();\r\n		}\r\n	});\r\n	\r\n</script>\r\n<div style="clear: both"></div>\r\n</div> <!--// end content -->'),
(521, '<div class="panel-content content">\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form" action="<{$xoops_url}>/modules/formulize/admin/ui.php?page=form&aid=<{$content.aid}>&fid=<{$content.fid}>&tab=permissions" method="post">\r\n<{php}>print $GLOBALS[''xoopsSecurity'']->getTokenHTML()<{/php}>\r\n<input type="hidden" name="formulize_admin_handler" value="form_permissions">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.fid}>">\r\n<{* form number is used to trigger the actual submission of this form after data has been saved, so we can pass this state info to the server *}>\r\n<input type="hidden" name="form_number" value=<{$number}>>\r\n<input type="hidden" name="tabs_selected" value="<{$number-1}>">\r\n<input type="hidden" name="reload" value="">\r\n<input type="hidden" name="conditionsdelete" value="">\r\n<input type="hidden" id="permscrollx" name="scrollx" value="">	\r\n\r\n<div class="accordion-box">\r\n		<div class="form-item">\r\n			<fieldset>\r\n                <legend><label class="question">Which groups do you want to set permissions for?</label></legend>\r\n                <select name="groups[]" id="groups" size=10 multiple>\r\n                    <{foreach from=$content.groups item=group}>\r\n                        <option value=<{$group.id}><{$group.selected}>><{$group.name}></option>\r\n                    <{/foreach}>\r\n                </select>\r\n								<br /><br />\r\n								<input type="button" name="showperms" value="Show permissions for these groups" />\r\n								<input type="hidden" name="useselection" value="" />\r\n								<br /><br />\r\n								<label class="question">List groups alphabetically or in creation order?</label>\r\n								<div class="form-radios radio-inline">\r\n                    <label for="alpha"><input type="radio" id="alpha" name="order" value="alpha"/>Alphabetical</label>\r\n                </div>\r\n                <div class="form-radios radio-inline">\r\n                    <label for="creation"><input type="radio" id="creation" name="order" value="creation" checked/>Creation order</label>\r\n                </div>\r\n								<br /><br />\r\n								<input type="button" name="savegrouplist" value="Save these groups as a list">\r\n								<input type="hidden" name="grouplistname" value="">\r\n								<input type="hidden" name="grouplistid" value="">\r\n            </fieldset>\r\n		</div>\r\n\r\n		<div class="form-item">\r\n			<fieldset>\r\n                <legend><label class="question">Select a list of groups at once</label></legend>\r\n                <select name="grouplists" id="grouplists" size=1>\r\n                    <{foreach from=$content.grouplists item=grouplist}>\r\n                        <option value=<{$grouplist.id}><{$grouplist.selected}>><{$grouplist.name}></option>\r\n                    <{/foreach}>\r\n                </select>\r\n								<input type="hidden" name="loadthislist" value="">\r\n								<input type="button" name="removegrouplist" value="Remove the selected list">\r\n								<input type="hidden" name="removelistid" value="">\r\n            </fieldset>\r\n		</div>\r\n		\r\n		<div class="form-item">\r\n			<fieldset>\r\n			<legend><label class="question">Set the same checkboxes for all groups?</label></legend>\r\n							<div class="form-radios radio-inline">\r\n                    <label for="same"><input type="radio" id="same" name="same_diff" value="same" />Yes, when I check a box for one group, check it for all groups</label>\r\n                </div>\r\n                <div class="form-radios radio-inline">\r\n                    <label for="different"><input type="radio" id="different" name="same_diff" value="different" />No, I will set each group individually</label>\r\n                </div>\r\n								<div class="description">\r\n									<p>You can change this setting at any time while you are adjusting the checkboxes.  Set it to <b>Yes</b>, to quickly set some checkboxes the same for all groups.  Change it to <b>No</b> when you need to set specific checkboxes for only certain groups.</p>\r\n								</div>\r\n		\r\n	</fieldset>\r\n		</div>\r\n\r\n</div>\r\n\r\n\r\n<div class="accordion-box">\r\n	\r\n	<{if $content.groupperms|@count == 0}>\r\n		<div class="form-item">\r\n		<fieldset>\r\n			<legend><label class="question">Select some groups to see their permissions</label></legend>\r\n		</fieldset>\r\n		</div>\r\n	<{/if}>\r\n	\r\n	\r\n	<{foreach from=$content.groupperms item=groupperm}>\r\n	<input type="hidden" name="group_list[]" value=<{$groupperm.id}>>\r\n	<div class="accordion-box">\r\n	<div class="form-item">\r\n		<fieldset>\r\n			<legend><label class="question"><{$groupperm.name}></label></legend>\r\n			<p><b>The basics:</b></p>\r\n			<div class="permissiongroup">\r\n			<input type="checkbox" class="view_form" name="<{$content.fid}>_<{$groupperm.id}>_view_form" value=1<{$groupperm.view_form}>>&nbsp;&nbsp;View the form<br />\r\n			<input type="checkbox" class="add_own_entry" name="<{$content.fid}>_<{$groupperm.id}>_add_own_entry" value=1<{$groupperm.add_own_entry}>>&nbsp;&nbsp;Create their own entries in the form<br />\r\n			<input type="checkbox" class="update_own_entry" name="<{$content.fid}>_<{$groupperm.id}>_update_own_entry" value=1<{$groupperm.update_own_entry}>>&nbsp;&nbsp;Update entries <i>made by themselves</i><br />\r\n			<input type="checkbox" class="update_other_entries" name="<{$content.fid}>_<{$groupperm.id}>_update_other_entries" value=1<{$groupperm.update_other_entries}>>&nbsp;&nbsp;Update entries <i>made by other people</i><br />\r\n			<input type="checkbox" class="delete_own_entry" name="<{$content.fid}>_<{$groupperm.id}>_delete_own_entry" value=1<{$groupperm.delete_own_entry}>>&nbsp;&nbsp;Delete entries <i>made by themselves</i><br />\r\n			<input type="checkbox" class="delete_other_entries" name="<{$content.fid}>_<{$groupperm.id}>_delete_other_entries" value=1<{$groupperm.delete_other_entries}>>&nbsp;&nbsp;Delete entries <i>made by other people</i><br />\r\n			</div>\r\n			<p><b>Visibility:</b></p>\r\n			<div class="permissiongroup">\r\n			<input type="checkbox" class="view_private_elements" name="<{$content.fid}>_<{$groupperm.id}>_view_private_elements" value=1<{$groupperm.view_private_elements}>>&nbsp;&nbsp;View elements in the form that are marked as "private"<br />\r\n			<input type="checkbox" name="<{$content.fid}>_<{$groupperm.id}>_dummy1" value=1 checked disabled>&nbsp;&nbsp;View their own entries (always on)<br />\r\n			<input type="checkbox" class="view_globalscope" name="<{$content.fid}>_<{$groupperm.id}>_view_globalscope" value=1<{$groupperm.view_globalscope}>>&nbsp;&nbsp;View entries by all other users in all groups<br />\r\n			<input type="checkbox" class="view_groupscope" name="<{$content.fid}>_<{$groupperm.id}>_view_groupscope" value=1<{$groupperm.view_groupscope}>>&nbsp;&nbsp;View entries by other users from these groups only:\r\n			<div class="groupselectionbox">\r\n				<select name="groupsscope_choice_<{$content.fid}>_<{$groupperm.id}>[]" size=6 multiple>\r\n				<option value=0<{$groupperm.groupscope_choice.0}>>All the groups the user is a member of, that can view the form</option>\r\n				<{foreach from=$content.groups item=group}>\r\n				<{assign var=groupid value=$group.id}>\r\n        <option value=<{$groupid}><{$groupperm.groupscope_choice.$groupid}>><{$group.name}></option>\r\n        <{/foreach}>\r\n				</select>\r\n			</div>\r\n	    <input type="checkbox" name="<{$content.fid}>_<{$groupperm.id}>_filterentries" value=1<{$groupperm.hasgroupfilter}> disabled>&nbsp;&nbsp;View only entries that meet these conditions:\r\n			<div class="groupselectionbox">\r\n			<{$groupperm.groupfilter}>\r\n			</div>\r\n			</div>\r\n			<p><b>Publishing "Saved Views" of form entries:</b></p>\r\n			<div class="permissiongroup">\r\n			<input type="checkbox" name="<{$content.fid}>_<{$groupperm.id}>_dummy2" value=1 checked disabled>&nbsp;&nbsp;Create, update, delete their own "saved views" (always on)<br />\r\n			<input type="checkbox" class="publish_reports" name="<{$content.fid}>_<{$groupperm.id}>_publish_reports" value=1<{$groupperm.publish_reports}>>&nbsp;&nbsp;Publish "saved views" for other users <i>in their group(s)</i><br />\r\n			<input type="checkbox" class="publish_globalscope" name="<{$content.fid}>_<{$groupperm.id}>_publish_globalscope" value=1<{$groupperm.publish_globalscope}>>&nbsp;&nbsp;Publish "saved views" for other users <i>in any group</i><br />\r\n			<input type="checkbox" class="update_other_reports" name="<{$content.fid}>_<{$groupperm.id}>_update_other_reports" value=1<{$groupperm.update_other_reports}>>&nbsp;&nbsp;Update "saved views" that other people have published<br />\r\n			<input type="checkbox" class="delete_other_reports" name="<{$content.fid}>_<{$groupperm.id}>_delete_other_reports" value=1<{$groupperm.delete_other_reports}>>&nbsp;&nbsp;Delete "saved views" that other people have published<br />\r\n			</div>\r\n			<p><b>Advanced options:</b></p>\r\n			<div class="permissiongroup">\r\n			<input type="checkbox" class="import_data" name="<{$content.fid}>_<{$groupperm.id}>_import_data" value=1<{$groupperm.import_data}>>&nbsp;&nbsp;Import data from a spreadsheet<br />\r\n			<input type="checkbox" class="set_notifications_for_others" name="<{$content.fid}>_<{$groupperm.id}>_set_notifications_for_others" value=1<{$groupperm.set_notifications_for_others}>>&nbsp;&nbsp;Create notifications that get sent to other users<br />\r\n			<input type="checkbox" class="add_proxy_entries" name="<{$content.fid}>_<{$groupperm.id}>_add_proxy_entries" value=1<{$groupperm.add_proxy_entries}>>&nbsp;&nbsp;Create entries on behalf of other users<br />\r\n			<input type="checkbox" class="update_entry_ownership" name="<{$content.fid}>_<{$groupperm.id}>_update_entry_ownership" value=1<{$groupperm.update_entry_ownership}>>&nbsp;&nbsp;Change the owner/creator of an existing entry<br />\r\n			<input type="checkbox" class="edit_form" name="<{$content.fid}>_<{$groupperm.id}>_edit_form" value=1<{$groupperm.edit_form}>>&nbsp;&nbsp;Alter this form''s configuration settings<br />\r\n			<input type="checkbox" class="delete_form" name="<{$content.fid}>_<{$groupperm.id}>_delete_form" value=1<{$groupperm.delete_form}>>&nbsp;&nbsp;Delete this form\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n	</div>\r\n	<{/foreach}>\r\n</div>\r\n\r\n</form>\r\n\r\n<div style="clear: both"></div>\r\n</div> <!--// end content -->\r\n\r\n<script type=''text/javascript''>\r\n\r\n$("#<{$content.order}>").attr(''checked'', true);\r\n$("#<{$content.samediff}>").attr(''checked'', true);\r\n\r\n$("[name=savegrouplist]").click(function () {\r\n	var grouplistname = prompt("Name of this group list", $("#grouplists option:selected").text());\r\n	if(grouplistname) {\r\n		$("[name=grouplistname]").val(grouplistname);\r\n		$("[name=grouplistid]").val($("#grouplists").val());\r\n		formulize_reload();\r\n	}\r\n  return false;\r\n});\r\n\r\n$("[name=removegrouplist]").click(function () {\r\n	var answer = confirm("Are you sure you want to delete the group list ''"+$("#grouplists option:selected").text()+"''?");\r\n	if(answer) {\r\n		$("[name=removelistid]").val($("#grouplists").val());\r\n		formulize_reload();\r\n	}\r\n  return false;\r\n});\r\n\r\n$("[name=grouplists]").change(function () {\r\n	$("[name=loadthislist]").val($("#grouplists").val());\r\n	formulize_reload();\r\n});\r\n\r\n$("[name=showperms]").click(function () {\r\n	$("[name=useselection]").val(1);\r\n	formulize_reload();\r\n});\r\n\r\n$("[name=order]").change(function () {\r\n	formulize_reload();\r\n});\r\n\r\n$("[name=addcon]").click(function () {\r\n	formulize_reload();\r\n});\r\n\r\n$(".conditionsdelete").click(function () {\r\n	$("[name=conditionsdelete]").val($(this).attr(''target''));\r\n	formulize_reload();\r\n	return false;\r\n});\r\n\r\n$("div.permissiongroup > input[type=checkbox]").click(function () {\r\n	if($(''input:radio[name=same_diff]:checked'').val() == "same") {\r\n		var checked = $(this).attr(''checked'');\r\n		var checkedclass = "."+$(this).attr(''class'');\r\n		$(checkedclass).attr(''checked'', checked);\r\n	}\r\n});\r\n\r\nfunction formulize_reload() {\r\n	$("[name=reload]").val(1);\r\n	$("#permscrollx").val($(window).scrollTop());\r\n	$(".savebutton").click();\r\n}\r\n\r\n</script>');
INSERT INTO `tplsource` (`tpl_id`, `tpl_source`) VALUES
(522, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="form_screens">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.fid}>">\r\n<input type="hidden" name="formulize_admin_aid" value="<{$content.aid}>">\r\n<input type="hidden" name="gotoscreen" value="">\r\n<input type="hidden" name="deletescreen" value="">\r\n	\r\n	\r\n\r\n\r\n<div class="panel-content content">\r\n\r\n	<p><i>Screens</i> let you show users different versions of the form, and the entries people have made in the form.  One screen might be a list of entries, another might be a control panel for administrators to edit and delete entries, another might be a multi-page version of a form.  You can have as many different screens as you want, all based on the same underlying form.</p>\r\n	<p>Each screen has its own URL, and can be included in any navigation structure you wish to use.  Screens can also be embedded in any PHP web page, anywhere on your web server, even inside other software like Wordpress or Drupal.  See the <i>Settings</i> page of each screen for details.</p>\r\n	<p>When someone visits a Formulize form, but no specific screen has been requested in the URL, then the user will get either the default list of entries screen, or the default form screen.  Formulize will figure out what the user should see, based on the configuration and permission settings for the form.</p>\r\n\r\n  <h2><a href="ui.php?page=screen&tab=settings&aid=<{$content.aid}>&fid=<{$content.fid}>&sid=new"><img src="../images/filenew2.png"> Create a new screen</a></h2>\r\n\r\n  <{* include form if exist screens exist *}>\r\n\r\n  <h2>Form Screens</h2>\r\n  <div class="form-item">\r\n    <table>\r\n	    <tr>\r\n		    <th>Name</th>\r\n				<th>id</th>\r\n		    <th>Type</th>\r\n		    <th>Default</th>\r\n		    <th>Action</th>\r\n	    </tr>\r\n      <{foreach from=$content.screens item=screen}>\r\n	      <tr>\r\n		      <td><label for="screens-defaultform-<{$screen.sid}>"><a class="configscreen" target="<{$screen.sid}>" href=""><img src="../images/kedit.png"> <{$screen.title}></a></label></td>\r\n					<td><{$screen.sid}></td>\r\n		      <td><{if $screen.type eq ''multiPage''}>Multi page<{else}>Regular<{/if}></td>\r\n		      <td><div class="form-radios"><input type="radio" id="screens-defaultform-<{$screen.sid}>" name="screens-defaultform" value="<{$screen.sid}>"<{if $screen.sid eq $content.defaultform}> checked="checked"<{/if}>/></div></td>\r\n  		    <td><a href="<{$xoops_url}>/modules/formulize/index.php?sid=<{$screen.sid}>" target="_blank"><img src="../images/kfind.png"> View</a>&nbsp;&nbsp;&nbsp;&nbsp;<a class="deletescreen" target="<{$screen.sid}>" href=""><img src="../images/editdelete.gif"> Remove</a></td>\r\n	      </tr>\r\n      <{/foreach}>\r\n    </table>\r\n  </div>\r\n\r\n  <h2>List Screens</h2>\r\n  <div class="form-item">\r\n    <table>\r\n	    <tr>\r\n		    <th>Name</th>\r\n				<th>id</th>\r\n		    <th>Default</th>\r\n		    <th>Action</th>\r\n	    </tr>\r\n      <{foreach from=$content.listOfEntries item=screen}>\r\n	      <tr>\r\n		      <td><label for="screens-defaultlist-<{$screen.sid}>"><a class="configscreen" target="<{$screen.sid}>" href=""><img src="../images/kedit.png"> <{$screen.title}></a></label></td>\r\n					<td><{$screen.sid}></td>\r\n		      <td><div class="form-radios"><input type="radio" id="screens-defaultlist-<{$screen.sid}>" name="screens-defaultlist" value="<{$screen.sid}>"<{if $screen.sid eq $content.defaultlist}> checked="checked"<{/if}>/></div></td>\r\n  		    <td><a href="<{$xoops_url}>/modules/formulize/index.php?sid=<{$screen.sid}>" target="_blank"><img src="../images/kfind.png"> View</a>&nbsp;&nbsp;&nbsp;&nbsp;<a class="deletescreen" target="<{$screen.sid}>" href=""><img src="../images/editdelete.gif"> Remove</a></td>\r\n	      </tr>\r\n      <{/foreach}>\r\n    </table>\r\n  </div>\r\n</div>\r\n\r\n</form>\r\n\r\n<script type="text/javascript">\r\n\r\n$(".configscreen").click(function() {\r\n	$("[name=gotoscreen]").val($(this).attr(''target''));\r\n	$(".savebutton").click();\r\n	return false;\r\n})\r\n\r\n$(".deletescreen").click(function() {\r\n	var answer = confirm("Are you sure you want to delete this screen?  All configuration settings for this screen will be lost!");\r\n	if(answer) {\r\n		$("[name=deletescreen]").val($(this).attr(''target''));\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n})\r\n\r\n</script>'),
(523, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form class="formulize-admin-form">\r\n<input type="hidden" name="formulize_admin_handler" value="form_elements">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.fid}>">\r\n<input type="hidden" name="cloneelement" value="">\r\n<input type="hidden" name="deleteelement" value="">\r\n<input type="hidden" name="convertelement" value="">\r\n<input type="hidden" name="reload_elements" value="">\r\n<input type="hidden" name="aid" value="<{$content.aid}>">	\r\n<input type="hidden" name="elementorder" value="">	\r\n	\r\n<div class="accordion-box">\r\n	<h2>Add elements to the form</h2>\r\n	<p>Click an element name to add it</p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=text"><img src="../images/filenew2.png"> Text box</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=textarea"><img src="../images/filenew2.png"> Multi-line text box</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=areamodif"><img src="../images/filenew2.png"> Text for display (left and right cells)</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=ib"><img src="../images/filenew2.png"> Text for display (spanning both cells)</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=select"><img src="../images/filenew2.png"> Select box (dropdowns and list boxes)</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=checkbox"><img src="../images/filenew2.png"> Check boxes</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=radio"><img src="../images/filenew2.png"> Radio buttons</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=yn"><img src="../images/filenew2.png"> Yes/No radio buttons</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=date"><img src="../images/filenew2.png"> Date box</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=subform"><img src="../images/filenew2.png"> Subform (another form with a relationship to this one)</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=grid"><img src="../images/filenew2.png"> Table of existing elements (place BEFORE the elements it contains)</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=derived"><img src="../images/filenew2.png"> Value derived from other elements</a></p>\r\n			<p><a href="ui.php?page=element&ele_id=new&fid=<{$content.fid}>&aid=<{$content.aid}>&type=colorpick"><img src="../images/filenew2.png"> Color picker</a></p>\r\n</div>\r\n\r\n<div class="accordion-box-wide">\r\n<h2>Manage the elements in the form</h2>\r\n<p>Click and drag the elements to re-order them</p>\r\n		<div id="sortable-list">\r\n<{include file="db:admin/ui-accordion.html" sectionTemplate="db:admin/form_elements_sections.html" sections=$content.elements}>\r\n		</div>\r\n</div>\r\n</form>\r\n<div style="clear: both;"></div>\r\n<script type="text/javascript">\r\n\r\n$(".clonelink").click(function () {\r\n	$("[name=cloneelement]").val($(this).attr(''target''));\r\n	$(".savebutton").click();\r\n	return false;\r\n});\r\n\r\n$(".deletelink").click(function () {\r\n	var answer = confirm("Are you sure you want to delete this element?  All data users have entered into this element will be lost!");\r\n	if(answer) {\r\n		$("[name=deleteelement]").val($(this).attr(''target''));\r\n		$("[name=reload_elements]").val(1);\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n});\r\n\r\n$(".converttotextarea").click(function () {\r\n	var answer = confirm("Convert this text box to a multi-line text box?");\r\n	if(answer) {\r\n		$("[name=convertelement]").val($(this).attr(''target''));\r\n		$("[name=reload_elements]").val(1);\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n});\r\n\r\n$(".converttotext").click(function () {\r\n	var answer = confirm("Convert this multi-line text box to a single-line text box?");\r\n	if(answer) {\r\n		$("[name=convertelement]").val($(this).attr(''target''));\r\n		$("[name=reload_elements]").val(1);\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n});\r\n$(".converttocheckbox").click(function () {\r\n	var answer = confirm("Convert these radio buttons to checkboxes?");\r\n	if(answer) {\r\n		$("[name=convertelement]").val($(this).attr(''target''));\r\n		$("[name=reload_elements]").val(1);\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n});\r\n$(".converttoradio").click(function () {\r\n	var answer = confirm("Convert these checkboxes to radio buttons?");\r\n	if(answer) {\r\n		$("[name=convertelement]").val($(this).attr(''target''));\r\n		$("[name=reload_elements]").val(1);\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n});\r\n\r\n$(".savebutton").click(function () {\r\n	$("[name=elementorder]").val($("#accordion-2").sortable(''serialize'')); \r\n});\r\n\r\n$("#accordion-2").bind( "sortupdate", function(event, ui) {\r\n  window.document.getElementById(''savewarning'').style.display = ''block'';\r\n});\r\n\r\n</script>'),
(524, '<div class="panel-content content">\r\n	<p><a href="ui.php?page=element&aid=<{$content.aid}>&ele_id=<{$sectionContent.ele_id}>"><img src="../images/kedit.png"> Configure</a>&nbsp;&nbsp;&nbsp;&nbsp;<a href="" class="clonelink" target="<{$sectionContent.ele_id}>"><img src="../images/clone.gif"> Clone</a>&nbsp;&nbsp;&nbsp;&nbsp;<a href="" class="deletelink" target="<{$sectionContent.ele_id}>"><img src="../images/editdelete.gif"> Delete</a></p>\r\n	\r\n<table>\r\n	<tr>\r\n		<th>Data handle</th>\r\n		<th>Type</th>\r\n		<th>Required</th>\r\n		<th>Display</th>\r\n		<th>Private</th>\r\n	</tr>\r\n	<tr>\r\n		<td><{$sectionContent.ele_handle}></td>\r\n		<td><{$sectionContent.ele_type}></td>\r\n		<td align="center"><input type="checkbox" <{if $sectionContent.ele_req}>checked="checked"<{/if}><{if $sectionContent.ele_req === false}>disabled<{/if}> value="1" name="elements-ele_req[<{$sectionContent.ele_id}>]"></td>\r\n		<td align="center"><{if is_numeric($sectionContent.ele_display)}><input type="checkbox" <{if $sectionContent.ele_display}>checked="checked"<{/if}> value="1" name="elements-ele_display[<{$sectionContent.ele_id}>]"><{else}><{$sectionContent.ele_display}><input type="hidden" name="customDisplayFlag[<{$sectionContent.ele_id}>]" value=1></input><{/if}></td>\r\n		<td align="center"><input type="checkbox" <{if $sectionContent.ele_private}>checked="checked"<{/if}>  value="1" name="elements-ele_private[<{$sectionContent.ele_id}>]"></td>\r\n	</tr>\r\n</table>\r\n<{if $sectionContent.converttext}>\r\n	<p><a href="" class="convertto<{$sectionContent.linktype}>" target="<{$sectionContent.ele_id}>"><{$sectionContent.converttext}></a></p>\r\n<{/if}>\r\n\r\n</div>'),
(525, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="form_advanced_calculations">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.fid}>">\r\n<input type="hidden" name="formulize_admin_aid" value="<{$content.aid}>">\r\n<input type="hidden" name="gotoadvanced_calculations" value="">\r\n<input type="hidden" name="deleteadvanced_calculations" value="">\r\n<input type="hidden" name="cloneadvanced_calculations" value="">\r\n\r\n\r\n<div class="panel-content content">\r\n\r\n	<p><i>Procedures</i> let you create a series of queries and logical steps, that get carried out on the data that users have submitted in the form.  You can use Procedures for advanced, multi-step calculations, or any other situation where a single query or single operation is not enough to get to the outcome you want.</p>\r\n\r\n  <h2><a name="newprocedure" href="ui.php?page=advanced-calculation&tab=settings&aid=<{$content.aid}>&fid=<{$content.fid}>&acid=new"><img src="../images/filenew2.png"> Create a new Procedure</a></h2>\r\n\r\n  <{* include form if exist advanced_calculations exist *}>\r\n\r\n  <h2>Procedures</h2>\r\n  <div class="form-item">\r\n    <table>\r\n	    <tr>\r\n		    <th>Name</th>\r\n		    <th>Action</th>\r\n	    </tr>\r\n      <{foreach from=$content.advanced_calculations item=advanced_calculation}>\r\n	      <tr>\r\n		      <td><label for="advanced-calculation-defaultform-<{$advanced_calculation.acid}>"><a class="configadvanced_calculation" target="<{$advanced_calculation.acid}>" href=""><img src="../images/kedit.png"> <{$advanced_calculation.name}></a></label></td>\r\n  		    <td><a class="cloneadvanced_calculation" target="<{$advanced_calculation.acid}>" href=""><img src="../images/clone.gif"> Clone</a>&nbsp;&nbsp;&nbsp;&nbsp;<a class="deleteadvanced_calculation" target="<{$advanced_calculation.acid}>" href=""><img src="../images/editdelete.gif"> Remove</a></td>\r\n	      </tr>\r\n      <{/foreach}>\r\n    </table>\r\n  </div>\r\n</div>\r\n\r\n</form>\r\n\r\n<script type="text/javascript">\r\n\r\n$(".configadvanced_calculation").click(function() {\r\n	$("[name=gotoadvanced_calculations]").val($(this).attr(''target''));\r\n	$(".savebutton").click();\r\n	return false;\r\n})\r\n\r\n$("[name=newprocedure]").click(function() {\r\n	$("[name=gotoadvanced_calculations]").val(''new'');\r\n	$(".savebutton").click();\r\n	return false;\r\n})\r\n\r\n$(".deleteadvanced_calculation").click(function() {\r\n	var answer = confirm("Are you sure you want to delete this procedure?  All settings for this procedure will be lost!");\r\n	if(answer) {\r\n		$("[name=deleteadvanced_calculations]").val($(this).attr(''target''));\r\n		$(".savebutton").click();\r\n	}\r\n	return false;\r\n})\r\n\r\n$(".cloneadvanced_calculation").click(function() {\r\n	$("[name=cloneadvanced_calculations]").val($(this).attr(''target''));\r\n	$(".savebutton").click();\r\n	return false;\r\n})\r\n\r\n</script>'),
(526, '<div class="panel-content content">\r\n<h2><a href="ui.php?page=relationship&tab=settings&aid=<{$content.aid}>&frid=new"><img src="../images/filenew2.png"> Create a new relationship</a></h2>\r\n</div>\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<input type="hidden" name="formulize_admin_handler" value="application_relationships">\r\n<input type="hidden" name="deleteframework" value="">\r\n<{include file="db:admin/ui-accordion.html" sectionTemplate="db:admin/application_relationships_sections.html" sections=$content.relationships}>\r\n</form>\r\n\r\n<script type="text/javascript">\r\n  $(".deleterelationship").click( function() {\r\n    var answer = confirm("Are you sure you want to delete this relationship, and all its links?");\r\n    if(answer) {\r\n      $("[name=deleteframework]").val($(this).attr(''target''));\r\n      $(".savebutton").click();\r\n    }\r\n    return false;\r\n  });\r\n  \r\n</script>'),
(527, '<div>\r\n<p><a href="ui.php?page=relationship&frid=<{$sectionContent.frid}>&aid=<{$content.aid}>"><img src="../images/kedit.png"> Configure this relationship</a>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a class="deleterelationship" target="<{$sectionContent.frid}>" href=""><img src="../images/editdelete.gif">&nbsp;&nbsp;Delete this relationship</a></p>\r\n</div>\r\n<div class="panel-content content">\r\n	<p><b>Links between forms in this relationship:</b></p>\r\n  <ul>\r\n	  <{foreach from=$sectionContent.links key=linkNumber item=link}>\r\n      <li><{$link.form1}> + <{$link.form2}> - <{$link.relationship}></li>\r\n  	<{/foreach}>\r\n  </ul>\r\n</div>'),
(528, '<script type=''text/javascript''>\r\n  $(document).ready(function() {\r\n    $("#dialog-common-values").dialog({ autoOpen: false, modal: true, width: 700, height: 200 });\r\n  });\r\n\r\n  $.ajaxSetup({  \r\n    cache: false  \r\n  });  \r\n\r\n\r\n  function checkForCommon(Obj, form1, form2, lid) {\r\n    for (var i=0; i < Obj.options.length; i++) {\r\n      if(Obj.options[i].selected && Obj.options[i].value == ''common'') {\r\n        $("#dialog-common-values").dialog(''open'');\r\n        $("#dialog-common-values-content").load(''<{$smarty.const.XOOPS_URL}>/modules/formulize/admin/frameCommonElements1.php?form1='' + form1 + ''&form2='' + form2 + ''&lid='' + lid);\r\n      }\r\n    }\r\n  }\r\n</script>\r\n\r\n<div id="dialog-common-values" title="<{$smarty.const._AM_FRAME_WHICH_ELEMENTS}>" style=''display:none''><div id="dialog-common-values-content"></div></div>\r\n\r\n\r\n<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-admin-form" class="formulize-admin-form">\r\n<{php}>print $GLOBALS[''xoopsSecurity'']->getTokenHTML()<{/php}>\r\n<input type="hidden" name="formulize_admin_handler" value="relationship_settings">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.frid}>">\r\n<input type="hidden" name="formulize_admin_op" value="">\r\n<input type="hidden" name="formulize_admin_lid" value="">\r\n\r\n\r\n<div class="panel-content content">\r\n  <fieldset>\r\n    <legend><{$smarty.const._AM_FRAME_ADDFORM}></legend>\r\n    <table>\r\n      <tr>\r\n        <th>\r\n          <{$smarty.const._AM_FRAME_AVAILFORMS1}>\r\n        </th>\r\n        <th>\r\n          <{$smarty.const._AM_FRAME_AVAILFORMS2}>\r\n        </th>\r\n        <th>\r\n          Action\r\n        </th>\r\n      </th>\r\n      <tr>\r\n        <td class=even valign=top>\r\n          <select name=relationships-fid1 size=1>\r\n            <{foreach from=$content.formoptions key=linkNumber item=formoption}>\r\n              <option value="<{$formoption.value}>"><{$formoption.name}></option>\r\n            <{/foreach}>\r\n          </select>\r\n        </td>\r\n        <td class=even valign=top>\r\n          <select name=relationships-fid2 size=1>\r\n            <{foreach from=$content.formoptions key=formoptionNumber item=formoption}>\r\n              <option value="<{$formoption.value}>"><{$formoption.name}></option>\r\n            <{/foreach}>\r\n          </select>\r\n        </td>\r\n        <td class=head valign=top>\r\n          <input type=submit class=formbutton name=addlink value=''<{$smarty.const._AM_FRAME_NEWFORMBUTTON}>''>\r\n        </td>\r\n      </tr>\r\n    </table>\r\n  </fieldset>\r\n\r\n  <br>\r\n\r\n  <{if count($content.links) lt 1}>\r\n    <fieldset>\r\n      <legend><{$smarty.const._AM_FRAME_NOFORMS}></legend>\r\n    </fieldset>\r\n  <{else}>\r\n    <input type=hidden name=relationships-common1choice value="">\r\n    <input type=hidden name=relationships-common2choice value="">\r\n    <input type=hidden name=relationships-common_fl_id value="">\r\n    <fieldset>\r\n      <legend><{$smarty.const._AM_FRAME_FORMSIN}></legend>\r\n      <table>\r\n        <tr>\r\n          <th>\r\n          </th>\r\n          <th>\r\n            <{$smarty.const._AM_FRAME_AVAILFORMS1}>\r\n          </th>\r\n          <th>\r\n            <{$smarty.const._AM_FRAME_AVAILFORMS2}>\r\n          </th>\r\n          <th>\r\n            <{$smarty.const._AM_FRAME_RELATIONSHIP}>\r\n          </th>\r\n          <th>\r\n            <{$smarty.const._AM_FRAME_LINKAGE}>\r\n          </th>\r\n          <th>\r\n            <{$smarty.const._AM_FRAME_DISPLAY}>\r\n          </th>\r\n        </tr>\r\n	      <{foreach from=$content.links key=linkNumber item=link}>\r\n          <tr class="<{cycle values=''even,odd''}>">\r\n            <td>\r\n              <a href="" class="deletethislink" target="<{$link.lid}>"><img src="../images/editdelete.gif"></a>\r\n            </td>\r\n            <td>\r\n              <{$link.form1}>\r\n            </td>\r\n            <td>\r\n              <{$link.form2}>\r\n            </td>\r\n             <td>\r\n              <select name=relationships-rel<{$link.lid}> size=1>\r\n                <option value="1"<{if $link.relationship eq 1}> selected="selected"<{/if}>><{$smarty.const._AM_FRAME_ONETOONE}></option>\r\n                <option value="2"<{if $link.relationship eq 2}> selected="selected"<{/if}>><{$smarty.const._AM_FRAME_ONETOMANY}></option>\r\n                <option value="3"<{if $link.relationship eq 3}> selected="selected"<{/if}>><{$smarty.const._AM_FRAME_MANYTOONE}></option>\r\n              </select>\r\n            </td>\r\n            <td>\r\n              <select name=relationships-linkages<{$link.lid}> id=linkages<{$link.lid}> size=1 onchange="javascript:checkForCommon(this.form.linkages<{$link.lid}>, ''<{$link.form1_id}>'', ''<{$link.form2_id}>'', ''<{$link.lid}>'');">\r\n                <option value=''0+0''<{if $link.key1 eq 0 and $link.key2 eq 0}> selected="selected"<{/if}>><{$smarty.const._AM_FRAME_UIDLINK}></option>\r\n                <{if $link.common neq 1}>\r\n                  <option value=''common''><{$smarty.const._AM_FRAME_COMMONLINK}></option>\r\n                <{/if}>\r\n                <{foreach from=$link.linkoptions key=linkoptionNumber item=linkoption}>\r\n                  <option value="<{$linkoption.value}>"<{if $linkoption.value == $link.linkselected}> selected="selected"<{/if}>><{$linkoption.name}></option>\r\n                <{/foreach}>\r\n              </select>\r\n              <{if $link.common eq 1}>\r\n                <input type=hidden name=relationships-preservecommon<{$link.lid}> value=<{$link.key1}>+<{$link.key2}>>\r\n              <{/if}>\r\n            </td>\r\n            <td align="center" class="checkbox-table-cell">\r\n              <input type="checkbox"<{if $link.unifiedDisplay}> checked="checked"<{/if}> value="1" name="relationships-display<{$link.lid}>">\r\n            </td>\r\n          </tr>\r\n	      <{/foreach}>\r\n      </table>\r\n    </fieldset>\r\n  <{/if}>\r\n</div>\r\n\r\n</form>\r\n\r\n\r\n<script type=''text/javascript''>\r\n$("[name=addlink]").click(function () {\r\n  $("[name=formulize_admin_op]").val(''addlink'');\r\n  $(".savebutton").click();\r\n  return false;\r\n});\r\n\r\n$(".deletethislink").click(function () {\r\n	var answer = confirm (''<{$smarty.const._AM_CONFIRM_DEL_FF_FORM}>'');\r\n	if (answer)	{\r\n    $("[name=formulize_admin_op]").val(''dellink'');\r\n    $("[name=formulize_admin_lid]").val($(this).attr(''target''));\r\n    $(".savebutton").click();\r\n	}\r\n  return false;\r\n});\r\n</script>'),
(529, '<script type=''text/javascript''>\r\nvar lid=<{$content.lid}>;\r\n\r\n$("[name=submitx]").click(function () {\r\n  var form1choice = $("#form1choice").find('':selected'');\r\n  var form2choice = $("#form2choice").find('':selected'');\r\n\r\n  var sourceLink = $("#linkages"+lid).find('':selected'');\r\n  \r\n  sourceLink.val( form1choice.val() + ''+'' + form2choice.val() + ''+common'' );\r\n  sourceLink.text( ''<{$smarty.const._AM_FRAME_COMMON_VALUES}>'' + form1choice.text() + '' & '' + form2choice.text() );\r\n\r\n  $("#dialog-common-values").dialog(''close'');\r\n\r\n  return false;\r\n});\r\n</script>\r\n<div class="panel-content content">\r\n  <table align="center" width="80%">\r\n    <tr>\r\n      <th>\r\n        <{$content.form1.name}>\r\n      </th>\r\n      <th>\r\n        <{$content.form2.name}>\r\n      </th>\r\n      <th>\r\n        Action\r\n      </th>\r\n    </tr>\r\n    <tr>\r\n      <td class=even>\r\n        <select id=form1choice name=form1choice size=1>\r\n          <{foreach from=$content.form1.elements key=elementNumber item=element}>\r\n            <option value="<{$elementNumber}>"><{$element}></option>\r\n          <{/foreach}>\r\n        </select>\r\n      </td>\r\n      <td class=even>\r\n        <select id=form2choice name=form2choice size=1>\r\n          <{foreach from=$content.form2.elements key=elementNumber item=element}>\r\n            <option value="<{$elementNumber}>"><{$element}></option>\r\n          <{/foreach}>\r\n        </select>\r\n      </td>\r\n      <td class=head>\r\n        <input type=submit class=formbutton name=submitx value=''<{$smarty.const._SUBMIT}>''>\r\n      </td>\r\n    </tr>\r\n  </table>\r\n</div>'),
(530, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-admin-form" class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="screen_settings">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.sid}>">\r\n<input type="hidden" name="formulize_admin_fid" value="<{$content.fid}>">\r\n<input type="hidden" name="aid" value="<{$content.aid}>">\r\n\r\n\r\n<div class="panel-content content">\r\n  <fieldset>\r\n    <legend>Settings for the Screen: <em><{$content.title}></em></legend>\r\n\r\n	  <div class="form-item">\r\n		  <label for="screens-title">Name of the screen:</label>\r\n		  <input type="text" id="screens-title" name="screens-title" value="<{$content.title}>"/>\r\n	  </div>\r\n	  <div class="form-item">\r\n		  <label for="screens-type">What kind of screen is this:</label>\r\n			\r\n	    <select id="screens-type" size="1" name="screens-type"<{if $content.sid neq ''new''}> disabled<{/if}>>\r\n		    <option value="listOfEntries"<{if $content.type eq ''listOfEntries''}> selected="selected"<{/if}>>List of entries</option>\r\n		    <option value="form"<{if $content.type eq ''form''}> selected="selected"<{/if}>>Regular form</option>\r\n		    <option value="multiPage"<{if $content.type eq ''multiPage''}> selected="selected"<{/if}>>Multi page form</option>\r\n	    </select>\r\n			<{if $content.sid neq ''new''}>\r\n				<input type="hidden" name="screens-type" value="<{$content.type}>">\r\n			<{/if}>\r\n	  </div>\r\n	  <div class="form-item">\r\n		  <label for="screens-frid">Is this screen based on the form alone, or on a relationship with another form?</label>\r\n		  <select id="screens-frid" name="screens-frid">\r\n			  <option value="0"<{if $content.frid eq 0}> selected="selected"<{/if}>>The form alone, no relationship</option>\r\n	      <{foreach from=$content.links key=linkNumber item=link}>\r\n          <option value="<{$link.id}>"<{if $content.frid eq $link.id}> selected="selected"<{/if}>><{$link.name}></option>\r\n      	<{/foreach}>\r\n		  </select>\r\n	  </div>\r\n  </fieldset>\r\n\r\n	<fieldset>\r\n		<legend>URL for this screen</legend>\r\n		<p>To give people access to this screen, send them to this URL:</p>\r\n		<blockquote><{$xoops_url}>/modules/formulize/index.php?sid=<{$content.sid}></blockquote>\r\n	</fieldset>\r\n	\r\n	<fieldset>\r\n		<legend>PHP code for including this screen anywhere</legend>\r\n		<p>You can embed this screen in any PHP application or web page that is running on the same web server.<br />Use this snippet of PHP code to include it:</p>\r\n		<blockquote class="code">include_once "<{$smarty.const.XOOPS_ROOT_PATH}>/mainfile.php";<br/>\r\n$formulize_screen_id = <{$content.sid}>;<br/>\r\ninclude "<{$smarty.const.XOOPS_ROOT_PATH}>/modules/formulize/index.php";</blockquote>\r\n	</fieldset>\r\n\r\n  <!--<fieldset>\r\n	  <legend>Menu</legend>\r\n\r\n    <div class="form-item">\r\n	    <label for="imenu"><input type="radio" id="imenu" name="menu" value="imenu"/>Use iMenu</label>\r\n	    <label for="internal"><input type="radio" id="internal" name="menu" value="internal"/>Use internal menu</label>\r\n	    <div class="description">Choose whether you will use XOOPS iMenu for assigning menu items or the internal menu.</div>\r\n	  </div>\r\n  </fieldset>-->\r\n\r\n  <fieldset>\r\n	  <legend>Should this screen use an anti-CSRF security token</legend>\r\n\r\n	  <div class="form-item">\r\n	    <label for="screens-useToken-yes"><input type="radio" id="screens-useToken" name="screens-useToken" value="1"<{if $content.useToken eq 1}> checked<{/if}>/>Yes</label>\r\n	    <label for="screens-useToken-no"><input type="radio" id="screens-useToken" name="screens-useToken" value="0"<{if $content.useToken eq 0}> checked<{/if}>/>No</label>\r\n	    <div class="description">The security token is a defense against cross-site request forgery attacks. However, it can cause problems if you are using an advanced Ajax-based UI in a List of Entries screen, and possibly other screen types.</div>\r\n	  </div>\r\n  </fieldset>\r\n\r\n  <!--<fieldset>\r\n	  <legend>Sample PHP Code</legend>\r\n\r\n	  <div class="form-item">\r\n		  <textarea name="code"/></textarea>\r\n		  <input type="submit" class="copybutton" name="copy-code" value="Copy code"/>\r\n		  <div class="description">Copy this sample PHP code somewhere.</div>\r\n	  </div>\r\n  </fieldset>-->\r\n</div>\r\n\r\n</form>'),
(531, '<div class="panel-content content">\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<input type="hidden" name="formulize_admin_handler" value="element_names">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.ele_id}>">\r\n<input type="hidden" name="formulize_form_id" value="<{$content.fid}>">\r\n<input type="hidden" name="aid" value="<{$content.aid}>">\r\n<input type="hidden" name="element_type" value="<{$content.type}>">\r\n<input type="hidden" name="reload_names_page" value="">\r\n\r\n\r\n	<div class="form-item required">\r\n   	<fieldset>\r\n      <legend><label for="elements-ele_caption" class="question">Caption</label></legend>\r\n			<input type="text" name="elements-ele_caption" class="required_formulize_element" value="<{$content.ele_caption}>" size=70>\r\n			<div class="description">\r\n				<p>The text that users see when this element appears on a form.</p>\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements-ele_colhead" class="question">Column Heading</label></legend>\r\n			<input type="text" name="elements-ele_colhead" value="<{$content.ele_colhead}>" size=70>\r\n			<div class="description">\r\n				<p>Optional. The text that users see when this element appears at the top of a column in a list.</p>\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements-ele_desc" class="question">Help text</label></legend>\r\n			<textarea name="elements-ele_desc" rows=4 cols=70><{$content.ele_desc}></textarea>\r\n			<div class="description">\r\n				<p>Optional. Descriptive or explanatory text that accompanies the element.</p>\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements-ele_handle" class="question">Data handle</label></legend>\r\n			<input type="text" name="elements-ele_handle" value="<{$content.ele_handle}>" size=70>\r\n			<div class="description">\r\n				<p>Optional. The name used to refer to this element in programming code and in the database.</p>\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n\r\n\r\n<div class="accordion-box">\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="orderpref" class="question">Position of this element</label></legend>\r\n			<select name="orderpref" size=1>\r\n				<option value="bottom">At the end of the form</option>\r\n				<option value="top"<{$content.firstelementorder}>>At the beginning of the form</option>\r\n				<{html_options options=$content.orderoptions selected=$content.defaultorder}>\r\n			</select>\r\n		</fieldset>\r\n	</div>\r\n</div>\r\n<{if $content.ele_req_on}>\r\n<div class="accordion-box">\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements-ele_req" class="question">Make this element "required" so users must give a response:</label></legend>\r\n			<p><input type="radio" name="elements-ele_req" value=1<{$content.ele_req_yes_on}>> Yes</p>\r\n			<p><input type="radio" name="elements-ele_req" value=0<{$content.ele_req_no_on}>> No</p>\r\n		</fieldset>\r\n	</div>\r\n</div>\r\n<{/if}>\r\n\r\n</form>\r\n<div style="clear: both;"></div>\r\n</div> <!--// end content -->\r\n\r\n<script type="text/javascript">\r\n$(".savebutton").click(function() {\r\n  if($("[name=elements-ele_caption]").val() == "") {\r\n		alert("Elements must have a caption!");\r\n		$("[name=elements-ele_caption]").focus();\r\n	}\r\n	<{* reload after ele_handle is blank, since it will be reset to the ele_id and that''s a state change we''ll need to reload to show the user *}>\r\n	if($("[name=elements-ele_handle]").val() == "") {\r\n		$("[name=reload_names_page]").val(1);\r\n	}\r\n});\r\n\r\n$("[name=elements-ele_handle]").keydown(function () {\r\n	$("[name=check_handle]").val(1);\r\n});\r\n\r\n$("[name=elements-ele_caption]").keydown(function () {\r\n	$("[name=reload_names_page]").val(1);\r\n});\r\n\r\n\r\n\r\n\r\n</script>'),
