(533, '<div class="panel-content content">\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<input type="hidden" name="formulize_admin_handler" value="element_display">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.ele_id}>">\r\n<input type="hidden" name="reload_element_pages" value="">\r\n<input type="hidden" class="conditionsdelete" name="conditionsdelete" value="">\r\n\r\n\r\n<div class="accordion-box">\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements_ele_display" class="question">Which groups should see this element?</label></legend>\r\n			<select name="elements_ele_display[]" size=8 multiple>\r\n				<option value="all"<{$content.ele_display.all}>>All groups with permission to view the form</option>\r\n				<option value="none"<{$content.ele_display.none}>>No groups</option>\r\n				<{foreach from=$content.groups item=group}>\r\n				<{assign var=groupid value=$group.id}>\r\n        <option value=<{$groupid}><{$content.ele_display.$groupid}>><{$group.name}></option>\r\n        <{/foreach}>\r\n			</select>\r\n		</fieldset>\r\n	</div>\r\n</div>\r\n\r\n<div class="accordion-box">\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements_ele_disabled" class="question">Disable this element for any groups?</label></legend>\r\n			<select name="elements_ele_disabled[]" size=8 multiple>\r\n				<option value="none"<{$content.ele_disabled.none}>>Disable for no groups</option>\r\n				<option value="all"<{$content.ele_disabled.all}>>Disable for all groups</option>\r\n				<{foreach from=$content.groups item=group}>\r\n				<{assign var=groupid value=$group.id}>\r\n        <option value=<{$groupid}><{$content.ele_disabled.$groupid}>><{$group.name}></option>\r\n        <{/foreach}>\r\n			</select>\r\n			<div class="description">\r\n				<p>Users will see this element if they are a member of any group for which the element is not disabled.  Only users who have all their groups selected here, will see the element as disabled.</p>\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n</div>\r\n\r\n<div class="accordion-box-wide">\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label for="elements_ele_filtersettings" class="question">Only display the element in the form if the entry being edited meets these conditions:</label></legend>\r\n			<div id="displayfilter">\r\n			<{$content.filtersettings}>\r\n			</div>\r\n			<div class="description">\r\n				<p>When an entry is saved, these conditions will be checked and this element will be displayed or not displayed on the next page load.  This feature is most useful on multipage form screens, which save the entry between pages.</p>\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n</div>\r\n\r\n<div class="accordion-box">\r\n	<div class="form-item">\r\n   	<fieldset>\r\n      <legend><label class="question">Additional display options:</label></legend>\r\n			<input type="checkbox" name="elements-ele_forcehidden" value="1"<{$content.ele_forcehidden}>> If this element is not displayed to a user, still save its value when the form is submitted.\r\n			<div class="description">\r\n				This can be useful for setting default values in a form, for elements the user should not interact with.\r\n			</div>\r\n			<br /><input type="checkbox" name="elements-ele_private" value="1"<{$content.ele_private}>> Only display this element to users who have permission to "view private elements".\r\n			<div class="description">\r\n				This can be useful for hiding information like phone numbers and other personal details from most other users.\r\n			</div>\r\n		</fieldset>\r\n	</div>\r\n</div>\r\n\r\n\r\n</form>\r\n\r\n<div style="clear: both;"></div>\r\n</div> <!--// end content -->\r\n\r\n<script type=''text/javascript''>\r\n\r\n$("div#displayfilter > input#addcon").click(function () {\r\n	$("[name=reload_element_pages]").val(1);\r\n  $(".savebutton").click();\r\n	return false;\r\n});\r\n\r\n$("div#displayfilter > a.conditionsdelete").click(function () {\r\n	$(".conditionsdelete").val($(this).attr(''target''));\r\n	$("[name=reload_element_pages]").val(1);\r\n  $(".savebutton").click();\r\n	return false;\r\n});\r\n\r\n</script>'),
(534, '<div class="panel-content content">\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<input type="hidden" name="formulize_admin_handler" value="element_advanced">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.ele_id}>">\r\n<input type="hidden" name="original_handle" value="<{$content.original_handle}>">\r\n\r\n<{if $content.datatypeui}>\r\n<div class="accordion-box-wide">\r\n<div class="form-item">\r\n		<fieldset>\r\n			<legend><label class="question">How should the data for this element be stored in the database?</label></legend>\r\n			<{$content.datatypeui}>\r\n			<div class="description">\r\n				<p>Elements that will contain only numbers should use a numeric type, so that sorting and calculations work correctly.  When in doubt, leave this value on its default setting.</p>\r\n			</div>\r\n		</fieldset>\r\n</div>\r\n</div>\r\n<{/if}>\r\n\r\n<{if $content.ele_encrypt_show}>  \r\n<div class="accordion-box">\r\n<div class="form-item">\r\n		<fieldset>\r\n			<legend><label for="elements-ele_encrypt" class="question">Encrypt this information in the database?</label></legend>\r\n      <p><input type="radio" name="elements-ele_encrypt" value=1<{$content.ele_encrypt_yes_on}>> Yes</p>\r\n			<p><input type="radio" name="elements-ele_encrypt" value=0<{$content.ele_encrypt_no_on}>> No</p>\r\n      <div class="description">\r\n				<p>To prevent anyone who has access to the database from reading the information users submit, you can encrypt the information before it is stored in the database.  Most features of Formulize work with encrypted information, but some unusual operations, like grouping calculation results by an encrypted field, don''t work.</p>\r\n        <p><b>Encryption is dependent on the current database password; if you change the database password, all your encrypted information will be inaccessible!</b></p>\r\n			</div>\r\n  </fieldset>\r\n</div>\r\n</div>\r\n<{/if}>\r\n  \r\n</form>\r\n\r\n<div style="clear: both;"></div>\r\n</div> <!--// end content -->'),
(535, '<div class="panel-content content">\r\n	<{include file="db:admin/element_optionlist.html}>\r\n\r\n    <div class="form-item">\r\n      <fieldset>\r\n        <legend><{$smarty.const._AM_ELE_DELIM_CHOICE}></legend>\r\n        <div class="form-radios">\r\n	        <label for="element-delimit-br"><input type="radio" id="br" name="element_delimit" value="br"/><{$smarty.const._MI_formulize_DELIMETER_BR}></label>\r\n        </div>\r\n        <div class="form-radios">\r\n	        <label for="element-delimit-space"><input type="radio" id="space" name="element_delimit" value="space"/><{$smarty.const._MI_formulize_DELIMETER_SPACE}></label>\r\n        </div>\r\n	      <div class="form-radios">\r\n		      <label for="element-delimit-custom" style="display:inline">\r\n            <input type="radio" id="custom" name="element_delimit" value="custom"/><{$smarty.const._MI_formulize_DELIMETER_CUSTOM}></label>\r\n      	    <input type="text" id="element-delim_custom" name="element_delim_custom" value="<{$content.ele_delim_custom_value}>" size="25" maxlength="255"/>\r\n	      </div>\r\n      </fieldset>\r\n    </div>\r\n  \r\n</div>\r\n\r\n\r\n<script type=''text/javascript''>\r\n\r\n$("#element-delimit-custom").click(function () {\r\n  $("#element-delim_custom").focus();\r\n});\r\n\r\n$("#element-delim_custom").click(function () {\r\n  $("#custom").attr("checked", "checked"); \r\n});\r\n\r\n$("#<{$content.ele_delim}>").attr(''checked'',1);\r\n\r\n</script>'),
(536, '<div class="panel-content content">\r\n  <fieldset>\r\n    <legend>What date should appear in this box by default?</legend>\r\n\r\n    <div class="form-item">\r\n	    <input type="text" id="element-date" name="elements-ele_value[0]" value="<{if $content.ele_value[0] eq ''''}>YYYY-mm-dd<{else}><{$content.ele_value[0]}><{/if}>" size="15" maxlength="15"/>\r\n    </div>\r\n		<div class="description">\r\n			<p>Leave as "YYYY-mm-dd" if you do not want to have a specific date show up as the default.</p>\r\n		</div>\r\n  </fieldset>\r\n</div>\r\n\r\n\r\n<script type=''text/javascript''>\r\n$(function()\r\n{\r\n  $(''#element-date'').datepicker({dateFormat: ''yy-mm-dd'', constrainInput: false});\r\n});\r\n</script>'),
(537, '<div class="panel-content content">\r\n\r\n    <div class="form-item">\r\n	    <fieldset>\r\n		    <legend><{$smarty.const._AM_ELE_DERIVED_CAP}></legend>\r\n	      <textarea id="elements-ele_value" name="elements-ele_value[0]" rows="5" cols="35"><{$content.ele_value[0]}></textarea>\r\n	      <div class="description">\r\n          <p><{$content.listofelementsoptions}> <input type=button name=addele value="<{$smarty.const._AM_ELE_DERIVED_ADD}>"></p>\r\n          <p><{$smarty.const._AM_ELE_DERIVED_DESC}></p>\r\n        </div>\r\n	    </fieldset>\r\n    </div>\r\n\r\n    <div class="form-item">\r\n	    <fieldset>\r\n		    <legend><{$smarty.const._AM_ELE_DERIVED_NUMBER_OPTS}></legend>\r\n		    <div class="form-item">\r\n			    <label for="elements-ele_value[1]"><{$smarty.const._AM_ELE_NUMBER_OPTS_DEC}></label><input type="text" id="elements-ele_value[1]" name="elements-ele_value[1]" value="<{$content.ele_value.1}>" size="2" maxlength="2"/>\r\n		    </div>\r\n		    <div class="form-item">\r\n			    <label for="elements-ele_value[2]"><{$smarty.const._AM_ELE_NUMBER_OPTS_PREFIX}></label><input type="text" id="elements-ele_value[2]" name="elements-ele_value[2]" value="<{$content.ele_value.2}>" size="5" maxlength="255"/>\r\n		    </div>\r\n		    <div class="form-item">\r\n			    <label for="elements-ele_value[3]"><{$smarty.const._AM_ELE_NUMBER_OPTS_DECSEP}></label><input type="text" id="elements-ele_value[3]" name="elements-ele_value[3]" value="<{$content.ele_value.3}>" size="5" maxlength="255"/>\r\n		    </div>\r\n		    <div class="form-item">\r\n			    <label for="elements-ele_value[4]"><{$smarty.const._AM_ELE_NUMBER_OPTS_SEP}></label><input type="text" id="elements-ele_value[4]" name="elements-ele_value[4]" value="<{$content.ele_value.4}>" size="5" maxlength="255"/>\r\n		    </div>\r\n		    <div class="description">\r\n			    <{$smarty.const._AM_ELE_NUMBER_OPTS_DESC}>\r\n		    </div>\r\n	    </fieldset>\r\n    </div>\r\n  \r\n</div>\r\n\r\n\r\n<script type="text/javascript">\r\n// http://plugins.jquery.com/project/caret-range\r\n/*\r\n * jQuery Caret Range plugin\r\n * Copyright (c) 2009 Matt Zabriskie\r\n * Released under the MIT and GPL licenses.\r\n */\r\n(function($) {\r\n	$.extend($.fn, {\r\n		caret: function (start, end) {\r\n			var elem = this[0];\r\n\r\n			if (elem) {							\r\n				// get caret range\r\n				if (typeof start == "undefined") {\r\n					if (elem.selectionStart) {\r\n						start = elem.selectionStart;\r\n						end = elem.selectionEnd;\r\n					}\r\n					else if (document.selection) {\r\n						var val = this.val();\r\n						var range = document.selection.createRange().duplicate();\r\n						range.moveEnd("character", val.length)\r\n						start = (range.text == "" ? val.length : val.lastIndexOf(range.text));\r\n\r\n						range = document.selection.createRange().duplicate();\r\n						range.moveStart("character", -val.length);\r\n						end = range.text.length;\r\n					}\r\n				}\r\n				// set caret range\r\n				else {\r\n					var val = this.val();\r\n\r\n					if (typeof start != "number") start = -1;\r\n					if (typeof end != "number") end = -1;\r\n					if (start < 0) start = 0;\r\n					if (end > val.length) end = val.length;\r\n					if (end < start) end = start;\r\n					if (start > end) start = end;\r\n\r\n					elem.focus();\r\n\r\n					if (elem.selectionStart) {\r\n						elem.selectionStart = start;\r\n						elem.selectionEnd = end;\r\n					}\r\n					else if (document.selection) {\r\n						var range = elem.createTextRange();\r\n						range.collapse(true);\r\n						range.moveStart("character", start);\r\n						range.moveEnd("character", end - start);\r\n						range.select();\r\n					}\r\n				}\r\n\r\n				return {start:start, end:end};\r\n			}\r\n		}\r\n	});\r\n})(jQuery);\r\n\r\n\r\n$("[name=addele]").click(function () {\r\n  //$("#elements-ele_value[0]").val( $("#elements-ele_value[0]").val() + ''"'' + $("#listofelementsoptions").find('':selected'').val() + ''"'' );\r\n\r\n  var input = $("#elements-ele_value");\r\n  var range = input.caret();\r\n  var value = input.val();\r\n  var text = ''"'' + $("#listofelementsoptions").find('':selected'').val() + ''"'';\r\n  input.val(value.substr(0, range.start) + text + value.substr(range.end, value.length));\r\n  input.caret(range.start + text.length);\r\n\r\n  return false;\r\n});\r\n</script>');
