(538, '<div class="panel-content content">\r\n\r\n    <div class="form-item">\r\n	    <fieldset>\r\n	    <legend><{$smarty.const._AM_ELE_GRID_HEADING}></legend>\r\n		    <div class="form-radios">\r\n			    <label for="caption"><input type="radio" id="caption" name="elements-ele_value[0]" value="caption"/><{$smarty.const._AM_ELE_GRID_HEADING_USE_CAPTION}></label>\r\n		    </div>\r\n		    <div class="form-radios">\r\n			    <label for="form"><input type="radio" id="form" name="elements-ele_value[0]" value="form"/><{$smarty.const._AM_ELE_GRID_HEADING_USE_FORM}></label>\r\n		    </div>\r\n		    <div class="form-radios">\r\n			    <label for="none"><input type="radio" id="none" name="elements-ele_value[0]" value="none"/><{$smarty.const._AM_ELE_GRID_HEADING_NONE}></label>\r\n		    </div>\r\n	    </fieldset>\r\n    </div>\r\n    <div class="form-item">\r\n	    <fieldset>\r\n	    <legend><{$smarty.const._AM_ELE_GRID_HEADING_SIDEORTOP}></legend>\r\n		    <div class="form-radios">\r\n			    <label for="side"><input type="radio" id="side" name="elements-ele_value[5]" value="1"/><{$smarty.const._AM_ELE_GRID_HEADING_SIDE}></label>\r\n		    </div>\r\n		    <div class="form-radios">\r\n			    <label for="above"><input type="radio" id="above" name="elements-ele_value[5]" value=""/><{$smarty.const._AM_ELE_GRID_HEADING_TOP}></label>\r\n		    </div>\r\n	    </fieldset>\r\n    </div>\r\n    <div class="form-item required">\r\n	    <label for="element-row"><{$smarty.const._AM_ELE_GRID_ROW_CAPTIONS}><em>*</em></label>\r\n	    <textarea id="element-row" name="elements-ele_value[1]" rows="5" cols="50"><{$content.ele_value[1]}></textarea>\r\n	    <div class="description">\r\n		    <{$smarty.const._AM_ELE_GRID_ROW_CAPTIONS_DESC}>\r\n	    </div>\r\n    </div>\r\n    <div class="form-item required">\r\n	    <label for="element-col"><{$smarty.const._AM_ELE_GRID_COL_CAPTIONS}><em>*</em></label>\r\n	    <textarea id="element-col" name="elements-ele_value[2]" rows="5" cols="50"><{$content.ele_valu