(558, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="screen_multipage_text">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.sid}>">\r\n\r\n\r\n<div class="panel-content content">\r\n  <fieldset>\r\n    <legend>Text for Multi page form: <em><{$content.title}></em></legend>\r\n\r\n	  <div class="form-item">\r\n		  <label for="screens-introtext"><{$smarty.const._AM_FORMULIZE_SCREEN_INTRO}></label>\r\n		  <textarea name="screens-introtext" rows="20"/><{$content.introtext}></textarea>\r\n		  <div class="description"></div>\r\n	  </div>\r\n	  <div class="form-item">\r\n		  <label for="screens-thankstext"><{$smarty.const._AM_FORMULIZE_SCREEN_THANKS}></label>\r\n		  <textarea name="screens-thankstext" rows="20"/><{$content.thankstext}></textarea>\r\n		  <div class="description"></div>\r\n	  </div>\r\n  </fieldset>\r\n</div>\r\n\r\n</form>'),
(559, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<script type=''text/javascript''>\r\n  $(document).ready(function() {\r\n    $("#dialog-page-settings").dialog({ autoOpen: false, modal: true, width: 950, height: 450, close: function(event, ui) {\r\n				var dialogEditingPage = $("[name=piforjquery]").val();\r\n				var newTitle = $("#screens-pagetitle_"+dialogEditingPage).val();\r\n				var saveWarningDisplay = $("#popupsavewarning").css("display");\r\n				if(saveWarningDisplay == "none") {\r\n					$("#drawer-4-"+dialogEditingPage+" .accordion-name").empty();\r\n					$("#drawer-4-"+dialogEditingPage+" .accordion-name").append(newTitle);\r\n				}\r\n			}\r\n		});\r\n  });\r\n\r\n  $.ajaxSetup({  \r\n    cache: false  \r\n  });  \r\n\r\n\r\n  function editPageSettings(pageNumber) {\r\n		$("#dialog-page-settings-content").empty();\r\n		$("#dialog-page-settings-content").append("<h1>Loading...</h1>");\r\n		$("#dialog-page-settings").dialog(''open'');\r\n    $("#dialog-page-settings-content").load(''<{$smarty.const.XOOPS_URL}>/modules/formulize/admin/screen_multipage_pages_settings.php?page='' + pageNumber + ''&sid=<{$content.sid}>'');\r\n  }\r\n</script>\r\n\r\n<div id="dialog-page-settings" title="Edit Page Settings" style="display:none"><div id="dialog-page-settings-content"></div></div>\r\n\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="screen_multipage_pages">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.sid}>">\r\n<input type="hidden" name="formulize_admin_op" value="">\r\n<input type="hidden" name="formulize_admin_index" value="">\r\n<input type="hidden" name="reload_multipage_pages" value="">\r\n<input type="hidden" name="pageorder" value="">\r\n<input type="hidden" name="conditionsdelete" value="">\r\n\r\n\r\n\r\n    <p><a name="addpage" href=""><img src="../images/filenew2.png"> Create a new page</a></p>\r\n    <h2>Manage the pages in this screen:</h2>\r\n    <p>Click and drag the pages to re-order them</p>\r\n    <!--<p><a href="ui.php?page=screen&tab=pages&aid=<{$content.aid}>&fid=<{$content.fid}>&sid=<{$content.sid}>&op=new">Create a new page</a></p>-->\r\n  \r\n<div id="sortable-list">\r\n<{include file="db:admin/ui-accordion.html" sectionTemplate="db:admin/screen_multipage_pages_sections.html" sections=$content.pages}>\r\n</div>\r\n\r\n\r\n</form>\r\n\r\n\r\n<script type=''text/javascript''>\r\n\r\n  $("[name=addpage]").click(function () {\r\n    $("[name=formulize_admin_op]").val(''addpage'');\r\n    $(".savebutton").click();\r\n    return false;\r\n  });\r\n  \r\n  \r\n	$("[name=editpage]").click(function () {\r\n		editPageSettings($(this).attr(''target''));\r\n		return false;\r\n	});\r\n	\r\n	$("[name=delpage]").click(function () {\r\n			var answer = confirm(''Are you sure you want to delete this page?'');\r\n			if (answer)	{\r\n		    $("[name=formulize_admin_op]").val(''delpage'');\r\n		    $("[name=formulize_admin_index]").val($(this).attr(''target''));\r\n		    $(".savebutton").click();\r\n			}\r\n		  return false;\r\n	});\r\n\r\n  $(".savebutton").click(function () {\r\n  	$("[name=pageorder]").val($("#accordion-4").sortable(''serialize'')); \r\n  });\r\n\r\n  $("#accordion-4").bind( "sortupdate", function(event, ui) {\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n  });\r\n	\r\n</script>'),
(560, '<p><a name="delpage" href="" target="<{$sectionContent.index}>"><img src="../images/editdelete.gif"> Delete this page</a></p>\r\n		<p><a name="editpage" href="" target="<{$sectionContent.index}>"><img src="../images/kedit.png"> Edit this page</a></p>\r\n		<p>Form Elements on this page:</p>\r\n		<ul>\r\n			<{foreach from=$sectionContent.elements item=thiselement}>\r\n			<li><{$thiselement}></li>	\r\n			<{/foreach}>\r\n		</ul>'),
(561, '<div>\r\n	<form name="popupform">\r\n	<input type="hidden" name="formulize_admin_handler" value="screen_multipage_pages">\r\n  <input type="hidden" name="formulize_admin_key" value="<{$sid}>">\r\n	<input type="hidden" name="reloadpopup" value="">\r\n	<input type="hidden" name="conditionsdelete" value="">\r\n	<input type="hidden" name="piforjquery" value=<{$pageIndex}>>\r\n	<div id=popupsavebutton><input type="button" class="savebuttonpopup" value="Save your changes"/></div>\r\n	<div id="popupsavewarning">You have unsaved changes!</div>\r\n	<div style="clear: both"></div>\r\n	<div class="accordion-box">\r\n	  <div class="form-item required">\r\n		  <label for="screens-pagetitle_<{$pageIndex}>">Title for page number <{$pageNumber}></label>\r\n      <input type="text" class="required_formulize_element" id="screens-pagetitle_<{$pageIndex}>" name="screens-pagetitle_<{$pageIndex}>" value="<{$pageTitle}>" size="30" maxlength="255"/>\r\n		  <div class="description"></div>\r\n	  </div>\r\n	  <div class="form-item">\r\n		  <label for="screens-page<{$pageIndex}>">Form elements to display on page <{$pageNumber}></label>\r\n	    <select id="screens-page<{$pageIndex}>" name="screens-page<{$pageIndex}>[]" size="10" multiple>\r\n        <{html_options options=$options selected=$pageElements}>\r\n	    </select>\r\n		  <div class="description"></div>\r\n	  </div>\r\n	</div>\r\n		\r\n	<div class="accordion-box">\r\n				<p>What conditions are there on the display of this page?</p>\r\n				<{$pageConditions}>\r\n				<div class="description"><p>If you don''t specify any conditions, the page will always be included in the form.</p></div>\r\n	</div>\r\n\r\n\r\n</form>\r\n</div>\r\n\r\n<script type="text/javascript">\r\n\r\n  $("input").change(function() {\r\n    window.document.getElementById(''popupsavewarning'').style.display = ''block'';\r\n    });\r\n  $("input[type=text]").keydown(function() {\r\n    window.document.getElementById(''popupsavewarning'').style.display = ''block'';\r\n    });\r\n  $("select").change(function() {\r\n    window.document.getElementById(''popupsavewarning'').style.display = ''block'';\r\n    });\r\n  $("textarea").keydown(function() {\r\n    window.document.getElementById(''popupsavewarning'').style.display = ''block'';\r\n    });\r\n\r\n\r\n$(".savebuttonpopup").click(function() {\r\n  $(".required_formulize_element").each(function() {\r\n    if($(this).val() == "") {\r\n      alert("Pages must have titles!");\r\n      $(this).focus();\r\n    }\r\n	});\r\n});\r\n\r\n$("[name=addcon]").click(function () {\r\n	$(".savebuttonpopup").click();\r\n	return false;\r\n});\r\n\r\n$(".conditionsdelete").click(function () {\r\n	$("[name=conditionsdelete]").val($(this).attr(''target''));\r\n  $(".savebuttonpopup").click();\r\n	return false;\r\n});\r\n\r\n	$(".savebuttonpopup").click(function() {\r\n    if(validateRequired()) {\r\n			var pagedata = window.document.getElementsByName("popupform");\r\n			$.post("save.php?popupsave=1", $(pagedata).serialize(), function(data) {\r\n				if(data) {\r\n					if(data.substr(0,10)=="/* eval */") {\r\n						eval(data);\r\n					} else {\r\n						alert(data);\r\n					}\r\n				}\r\n				window.document.getElementById(''popupsavewarning'').style.display = ''none'';\r\n				\r\n			});\r\n    }\r\n    $(".savebuttonpopup").blur();\r\n  });\r\n		\r\n	function reloadPopup() {\r\n		$("#dialog-page-settings-content").load(''<{$smarty.const.XOOPS_URL}>/modules/formulize/admin/screen_multipage_pages_settings.php?page=<{$pageIndex}>&sid=<{$sid}>'');\r\n	}\r\n\r\n</script>'),
(562, '<{if $content.type!="select"}><fieldset>\r\n<legend><{$smarty.const._AM_ELE_OPT}></legend><{/if}>\r\n<p><input type="button" class="formButton" name="addoption" value="<{$smarty.const._AM_ELE_ADD_OPT_SUBMIT}> 1"</p>\r\n<div class="optionlist">\r\n<{if $content.useroptions|@count > 0}>\r\n  <{foreach from=$content.useroptions key=text item=checked name=optionsloop}>\r\n  <p class="useroptions" name="<{$smarty.foreach.optionsloop.index}>"><{if $content.type == "radio"}><input type="radio" name="defaultoption" value=<{$smarty.foreach.optionsloop.index}> <{if $checked}>checked<{/if}>></input><{else}><input type="checkbox" name="defaultoption[<{$smarty.foreach.optionsloop.index}>]" value=<{$smarty.foreach.optionsloop.index}> <{if $checked}>checked<{/if}>></input><{/if}>&nbsp;&nbsp;<input type="text" name="ele_value[<{$smarty.foreach.optionsloop.index}>]" value="<{$text}>"></input></p>\r\n  <{/foreach}>\r\n<{else}>\r\n  <p class="useroptions" name="0"><{if $content.type == "radio"}><input type="radio" name="defaultoption" value=0></input><{else}><input type="checkbox" name="defaultoption[0]" value=0></input><{/if}>&nbsp;&nbsp;<input type="text" name="ele_value[0]"></input></p>\r\n<{/if}>\r\n</div>\r\n\r\n<{if $content.type == "radio"}>\r\n<p><input type="button" class="formButton" name="cleardef" value="<{$smarty.const._AM_CLEAR_DEFAULT}>"/></p>\r\n<{/if}>\r\n\r\n<div class="description">\r\n  <{if $content.type == "radio"}><p><{$smarty.const._AM_ELE_OPT_DESC2}></p><p><{$smarty.const._AM_ELE_OTHER}></p><{/if}>\r\n  <{if $content.type == "checkbox"}><p><{$smarty.const._AM_ELE_OPT_DESC_CHECKBOXES}></p><p><{$smarty.const._AM_ELE_OTHER}></p><{/if}>\r\n  <{if $content.type == "select"}><p><{$smarty.const._AM_ELE_OPT_DESC}><{$smarty.const._AM_ELE_OPT_DESC1}></p><{/if}>\r\n  <p><{$smarty.const._AM_ELE_OPT_UITEXT}></p>\r\n</div>\r\n\r\n</fieldset>\r\n\r\n<fieldset>\r\n  <legend>Resynch existing entries with any option changes?</legend>\r\n  <p><label for="element-changeuservalues">\r\n<input type="checkbox" id="element-changeuservalues" name="changeuservalues" value="1"/>\r\n<{$smarty.const._AM_ELE_OPT_CHANGEUSERVALUES}></label></p>\r\n</fieldset>\r\n\r\n<script type="text/javascript">\r\n  $("[name=addoption]").click(function (){\r\n    number = $(".useroptions:last").attr(''name'');\r\n    number = parseInt(number) + 1;\r\n    appendContents1 = ''<{if $content.type == "radio"}><input type="radio" name="defaultoption" value=''+number+''></input><{else}><input type="checkbox" name="defaultoption[''+number+'']" value=''+number+''></input><{/if}>'';\r\n    appendContents2 = ''<input type="text" name="ele_value[''+number+'']"></input>'';\r\n    $(".optionlist").append(''<p class="useroptions" name="''+number+''"></p>'');\r\n    $(".useroptions:last").append(appendContents1);\r\n    $(".useroptions:last").append(''&nbsp;&nbsp;'');\r\n    $(".useroptions:last").append(appendContents2);\r\n    $("[name=addoption]").blur();\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n  });\r\n  \r\n  <{if $content.type == "radio"}>\r\n  $("[name=cleardef]").click(function () {\r\n    $("[name=defaultoption]").attr(''checked'',0);\r\n    $("[name=cleardef]").blur();\r\n  });\r\n  <{/if}>\r\n\r\n</script>'),
(563, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-admin-form" class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="advanced_calculation_settings">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.acid}>">\r\n<input type="hidden" name="formulize_admin_fid" value="<{$content.fid}>">\r\n<input type="hidden" name="aid" value="<{$content.aid}>">\r\n\r\n\r\n<div class="panel-content content">\r\n  <fieldset>\r\n    <legend>Settings for the Procedure: <em><{$content.name}></em></legend>\r\n\r\n	  <div class="form-item">\r\n		  <label for="name">Name of the Procedure:</label>\r\n		  <input type="text" id="advcalc-name" name="advcalc-name" value="<{$content.name}>"/>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n		  <label for="description">Description of the Procedure:</label>\r\n	    <textarea id="advcalc-description" name="advcalc-description" rows="5" cols="35"><{$content.description}></textarea>\r\n	  </div>\r\n  </fieldset>\r\n</div>\r\n\r\n</form>'),
(564, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-admin-form" class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="advanced_calculation_input_output">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.acid}>">\r\n<input type="hidden" name="formulize_admin_fid" value="<{$content.fid}>">\r\n<input type="hidden" name="aid" value="<{$content.aid}>">\r\n\r\n\r\n<div class="panel-content content">\r\n  <fieldset>\r\n    <legend>Input/Output for the Procedure: <em><{$content.name}></em></legend>\r\n\r\n	  <div class="form-item">\r\n		  <label for="input">PHP code for defining any initial values (before the steps are evaluated)</label><br />\r\n	    <textarea id="advcalc-input" name="advcalc-input" rows="5" cols="35"><{$content.input}></textarea>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n		  <label for="output">PHP code for outputing results to the screen</label><br />\r\n	    <textarea id="advcalc-output" name="advcalc-output" rows="10" cols="35"><{$content.output}></textarea>\r\n	  </div>\r\n  </fieldset>\r\n</div>\r\n\r\n</form>'),
(565, '<{* form elements must be named with their object name hyphen field name *}>\r\n<{* no other elements should have hyphens, since that tells the saving system that this is a property of an object to update *}>\r\n<{* securitytoken should be part of the form *}>\r\n<{* formulize_admin_handler and formulize_admin_key are required, to tell what the name of the save handling file is, and what the key is that we''re inserting/updating on *}>\r\n\r\n<form id="form-<{$number}>" class="formulize-admin-form">\r\n<{$securitytoken}>\r\n<input type="hidden" name="formulize_admin_handler" value="advanced_calculation_steps">\r\n<input type="hidden" name="formulize_admin_key" value="<{$content.acid}>">\r\n<input type="hidden" name="formulize_admin_op" value="">\r\n<input type="hidden" name="formulize_admin_index" value="">\r\n<input type="hidden" name="reload_advanced_calculation_steps" value="">\r\n<input type="hidden" name="steporder" value="">\r\n<input type="hidden" name="conditionsdelete" value="">\r\n\r\n\r\n\r\n    <p><a name="addstep" href=""><img src="../images/filenew2.png"> Create a new step</a></p>\r\n    <h2>Manage the steps in this Procedure:</h2>\r\n    <p>Click and drag the steps to re-order them</p>\r\n    <!--<p><a href="ui.php?page=screen&tab=steps&aid=<{$content.aid}>&fid=<{$content.fid}>&acid=<{$content.acid}>&op=new">Create a new step</a></p>-->\r\n  \r\n<div id="sortable-list">\r\n<{include file="db:admin/ui-accordion.html" sectionTemplate="db:admin/advanced_calculation_steps_sections.html" sections=$content.steps}>\r\n</div>\r\n\r\n\r\n</form>\r\n\r\n\r\n<script type=''text/javascript''>\r\n\r\n$(".savebutton").click(function() {\r\n  $(".required_formulize_element").each(function() {\r\n    if($(this).val() == "" && $(this).hasClass(''steptitle'')) {\r\n      alert("Steps must have names!");\r\n      $(this).focus();\r\n    }\r\n	});\r\n});\r\n\r\n$("[name=addcon]").click(function () {\r\n	$("[name=reload_advanced_calculation_steps]").val(1);\r\n  $(".savebutton").click();\r\n	return false;\r\n});\r\n\r\n$(".conditionsdelete").click(function () {\r\n	$("[name=conditionsdelete]").val($(this).attr(''target''));\r\n	$("[name=reload_advanced_calculation_steps]").val(1);\r\n  $(".savebutton").click();\r\n	return false;\r\n});\r\n\r\n\r\n  $(".steptitle").keydown(function () {\r\n    $("[name=reload_advanced_calculation_steps]").val(1);\r\n  });\r\n  $(".condition_term").keydown(function () {\r\n    $("[name=reload_advanced_calculation_steps]").val(1);\r\n  });\r\n  \r\n\r\n  $("[name=addstep]").click(function () {\r\n    $("[name=formulize_admin_op]").val(''addstep'');\r\n    $(".savebutton").click();\r\n    return false;\r\n  });\r\n  \r\n  \r\n	$("[name=delstep]").click(function () {\r\n			var answer = confirm(''Are you sure you want to delete this step?'');\r\n			if (answer)	{\r\n		    $("[name=formulize_admin_op]").val(''delstep'');\r\n		    $("[name=formulize_admin_index]").val($(this).attr(''target''));\r\n		    $(".savebutton").click();\r\n			}\r\n		  return false;\r\n	});\r\n  \r\n	$("[name=clonestep]").click(function () {\r\n    $("[name=formulize_admin_op]").val(''clonestep'');\r\n    $("[name=formulize_admin_index]").val($(this).attr(''target''));\r\n    $(".savebutton").click();\r\n    return false;\r\n	});\r\n\r\n  $(".savebutton").click(function () {\r\n  	$("[name=steporder]").val($("#accordion-3").sortable(''serialize'')); \r\n  });\r\n\r\n  $("#accordion-3").bind( "sortupdate", function(event, ui) {\r\n    window.document.getElementById(''savewarning'').style.display = ''block'';\r\n  });\r\n\r\n</script>'),
(566, '<p>\r\n      <a name="clonestep" href="" target="<{$sectionContent.index}>"><img src="../images/clone.gif"> Clone this step</a>\r\n      <a name="delstep" href="" target="<{$sectionContent.index}>"><img src="../images/editdelete.gif"> Delete this step</a>\r\n    </p>\r\n\r\n	  <div class="form-item required">\r\n		  <label for="advcalc-steptitle_<{$sectionContent.index}>">Title for step number <{$sectionContent.number}></label>\r\n      <input type="text" class="required_formulize_element" id="advcalc-steptitle_<{$sectionContent.index}>" name="advcalc-steptitle_<{$sectionContent.index}>" value="<{$sectionContent.title}>" size="30" maxlength="255"/>\r\n		  <div class="description"></div>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n		  <label for="input">Description of this step</label><br />\r\n	    <textarea id="advcalc-description_<{$sectionContent.index}>" name="advcalc-description_<{$sectionContent.index}>" rows="5" cols="35"><{$sectionContent.steps.description}></textarea>\r\n		  <div class="description"></div>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n		  <label for="input">Use this SQL to get data from the database</label><br />\r\n	    <textarea id="advcalc-sql_<{$sectionContent.index}>" name="advcalc-sql_<{$sectionContent.index}>" rows="15" cols="80"><{$sectionContent.steps.sql}></textarea>\r\n		  <div class="description">You can use any previously defined PHP variable in the SQL, including ones defined in previous steps.  Use $fromBaseQuery instead of "FROM tablename WHERE" to have your query use the current search and filtering settings that the user has specified in Formulize -- if you do this you MUST NOT use WHERE in the SQL ($fromBaseQuery already includes it) and you MUST start your own where clause statement(s) with AND (since they will be appended to the end of the Formulize base query''s where clause).  Use "{foreach $array as $key=>$value; BOOLEAN }SQL INCLUDING $key AND/OR $value{/foreach}" to add a certain piece of SQL once for every value in a previously defined array.  The BOOLEAN can be AND or OR, depending on which you want used between each instance of the SQL.</div>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n		  <label for="input">Run this PHP code before the SQL</label><br />\r\n	    <textarea id="advcalc-preCalculate_<{$sectionContent.index}>" name="advcalc-preCalculate_<{$sectionContent.index}>" rows="15" cols="80"><{$sectionContent.steps.preCalculate}></textarea>\r\n		  <div class="description"></div>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n			<label>Run this PHP code to process each record returned by the SQL</label><br />\r\n		  <textarea id="advcalc-calculate_<{$sectionContent.index}>" name="advcalc-calculate_<{$sectionContent.index}>" rows="15" cols="80"><{$sectionContent.steps.calculate}></textarea>\r\n		  <div class="description">use $row[X] or $array[fieldname] to refer to the values in the record.  X would be the order of the fields starting at 0, ie: use 2 to refer to the third field selected in the SQL.  fieldname would be the actual name of the field as selected in the SQL.</div>\r\n	  </div>\r\n\r\n	  <div class="form-item">\r\n		  <label for="input">Run this PHP code after processing all the records</label><br />\r\n	    <textarea id="advcalc-postCalculate_<{$sectionContent.index}>" name="advcalc-postCalculate_<{$sectionContent.index}>" rows="15" cols="80"><{$sectionContent.steps.postCalculate}></textarea>\r\n		  <div class="description"></div>\r\n	  </div>');
