CREATE TABLE imenu (  id int(5) unsigned NOT NULL auto_increment,  title varchar(150) NOT NULL default '',  hide tinyint(1) unsigned NOT NULL default '0',  link varchar(255) default NULL,  weight tinyint(4) unsigned NOT NULL default '0',  target varchar(10) default NULL,  groups text default NULL,  parent int(5) unsigned NOT NULL default '0',  PRIMARY KEY (id)) TYPE=MyISAM;